INSERT INTO lubadus_valdkond(valdkondid,pealkiri) VALUES (22,'SPORT JA KEHAKULTUUR');
INSERT INTO lubadus_valdkond(valdkondid,pealkiri) VALUES (28,'RIIGIKAITSE- JA JULGEOLEKUPOLIITIKA');
INSERT INTO lubadus_valdkond(valdkondid,pealkiri) VALUES (1,'KONKURENTSIVÕIMELINE MAJANDUSKESKKOND');
INSERT INTO lubadus_valdkond(valdkondid,pealkiri) VALUES (11,'PERESÕBRALIK RIIK');
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (1, 'Valitsusliidu majanduspoliitika eesmärk on Eesti inimeste elatustaseme tõus. Selle eelduseks on stabiilne majanduskasv ja konkurentsivõimeline majandusstruktuur – just neist sõltuvad uued töökohad, pensionitõus, teenused lastega peredele, turvalisus ja heal tasemel haridus. Eesti majanduskasvu võtmeks on edenemine ülemaailmses väärtusahelas – Eestis tehtu ja loodu peab muutuma oluliselt väärtuslikumaks ja konkurentsivõimelisemaks. Töö tootlikkus ja hind peab kasvama – vaid sellisel juhul saavad kasvada ettevõtlustulu ja palgad. Prioriteetseks on just inimeste sissetulekute kasv, mis pikemas perspektiivis tagab ka avalike teenuste osutamiseks vajalike vahendite piisavuse.', 1);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (2, 'Riik panustab majanduskasvu atraktiivse ja usaldusväärse keskkonna loomisega: hariduse, teaduse ja kultuuri arendamisega, tööturupoliitika, taristu-, IT-, energia- ja transpordipoliitika, õiguspoliitika ja majandusdiplomaatiaga. Kõiki riigi ja kohalike omavalitsuste poolt ettevõetavaid samme, mis mõjutavad majanduskeskkonda, tuleb hinnata nende mõju alusel Eesti rahvusvahelisele konkurentsivõimele.', 1);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (3, 'Riigi suur vastutus, mis võimaldab seda keskkonda rahastada, on elujõuline riigieelarve, stabiilne maksusüsteem, võimalikult madal ning majanduslikult mõistlikult jaotuv maksukoormus. Tuleb luua olukord, kus halbadele aegadele majanduses ei pea reageerima ühegi maksu tõstmisega.', 1);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (4, 'Maksukoormuse alandamine ning teised konkurentsivõimet tõstvad meetmed peavad sõltuma eelarve struktuurse ülejäägi suurusest.', 1);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (5, 'Valitsusliit viib läbi struktuurseid reforme ja poliitikaid, mis lähtuvad järgmistest põhimõtetest:', 1);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 6, '1. Eesti kui investeeringute ja uute töökohtade sihtmaa rahandusliku usaldusväärsuse süvendamine:', 2);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (6, 7, 'viime valitsussektori hiljemalt aastaks 2014 struktuursesse eelarve ülejääki ning hoiame seejärel ülejääki kõigil kasvuaastatel;', 2);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (6, 8, 'kasvatame reserve, mis lubavad võimalikud langusaastad valutult üle elada ka tulevikus;', 2);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (6, 9, 'lisame eelarve baasseadusesse valitsussektori eelarve tasakaalu nõude;', 2);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (6, 10, 'valitsussektori rahavoo ja võlakohustuste tõhusamaks juhtimiseks koondame järk-järgult valitsussektorisse kuuluvate juriidiliste isikute (v.a. kohalike omavalitsuste) vahendite arveldamise ja nende haldamise riigikassasse.', 2);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 11, '2. Lihtsa ja ühetaolise maksukeskkonna säilitamine:', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (11, 12, 'jätkame lihtsa ja ettevõtlikkust soodustava ühetaolise üksikisiku tulumaksuga ning välistame astmelise tulumaksusüsteemi kehtestamise;', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (11, 13, 'säilitame tulumaksuvabastuse ettevõttesse tagasi investeeritud kasumilt;', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (11, 14, 'järgime põhimõtet, et sotsiaalvajadusi rahastatakse kogutud maksudest, mitte maksude kogumatajätmisest. Reformime erimärgistatud kütuse maksuerisuste süsteemi, pakkudes teatud valdkondades asemele otsetoetusi. Langetame 1. jaanuarist 2012 tulumaksusoodustuse ülempiiri 1920 euroni;', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (11, 15, 'kaalume alkoholiaktsiisi kohaldamist ka kosmeetikatoodetes sisalduvale alkoholile.', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 16, '3. Maksukoormuse vähendamine, sealjuures langetades ennekõike tööjõu makse kõrge tootlikkusega töökohtade tekkeks:', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (16, 17, 'alates 2013 vähendame töötuskindlustusmakse määra;', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (16, 18, 'kehtestame 1. jaanuarist 2014 sotsiaalmaksu (v.a. ravikindlustuse osa) lae: töötasu osalt, mis ületab 4000 eurot kuus ei maksta sotsiaalmaksu;', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (16, 19, 'vähendame 1. jaanuarist 2015 tulumaksumäära 20%ni;', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (16, 20, 'ei käsitle kulutusi tööga seotud tasemekoolituselt erisoodustusena;', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (16, 21, 'koduomanike maksukoormuse vähendamiseks kaotame alates 1. jaanuarist 2013 maamaksu kodu alusele maale tiheasustusega kuni 1500m2 ja hajaasustusega piirkonnas kuni 2 ha ulatuses.', 3);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 22, '4. Tööturu turvalise paindlikkuse suurendamine:', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (22, 23, 'viime läbi eri- ja sooduspensionide reformi. Loome tööõnnetus- ja kutsehaiguskindlustussüsteemi, mis motiveerib töötajaid hoolima oma tervisest ja tööandjaid pakkuma oma töötajaile tervislikku töökeskkonda;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (22, 24, 'toetame tootlikkuse suurendamisele suunatud töötajate ümber- ja täiendõpet, ergutame ülikoole ja ettevõtjaid osalema senisest jõulisemalt täiendkoolituse turul, et viia elukestvas õppes osalemine Põhjamaade tasemele;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (22, 25, 'aktiivsete tööturumeetmete abil toetame puuetega inimeste algatusi luua koostöös ettevõtetega uusi töökohti;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (22, 26, 'analüüsime Töölepingu seaduse senist mõju eesmärgiga suurendada tööturu turvalist paindlikkust.', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 27, '5. Investeeringuid ning rahvusvahelist äri ligitõmbava sotsiaalse keskkonna arendamine:', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (27, 28, 'rakendame haridusprogrammi Põhjamaade tasemel kvaliteetse hariduse andmiseks, muuhulgas edendame Euroopa koole välisspetsialistide laste jaoks ja rahvusvahelist bakalaureuseõpet;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (27, 29, 'jätkame Eesti kujundamist turvalise ja perekeskse elukeskkonnaga riigiks;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (27, 30, 'edendame Eesti ühiskonna üldist avatust ja sallivust;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (27, 31, 'jätkame atraktiivse kultuurikeskkonna loomist Eestimaa erinevates piirkondades, mis aitab arendada turismivaldkonda;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (27, 32, 'Eesti majanduse konkurentsivõime tõstmiseks loome soodsa keskkonna välistudengite ja tippspetsialistide Eestisse tulekuks. See aitab Eestisse luua teadus- ja kompetentsikeskusi ning tagada ettevõtetele kõrgema kvaliteediga tööjõudu;', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (27, 33, 'oleme vastu väheste oskustega võõrtööjõu massilisele sissetoomisele.', 4);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 34, '6. Turutõkete kõrvaldamine ja ausa konkurentsi tagamine:', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (34, 35, 'võitlus kartellide ja monopolidega, kes takistavad konkurentide turulepääsu ja vähendavad sellega majanduse tõhusust ning tõstavad tarbijahindu, on riiklik prioriteet.', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 36, '7. Bürokraatia ja aruandluse vähendamine:', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (36, 37, 'vähendame bürokraatiat ja aruandlust, sealhulgas võimaldame alla 100 000 euro suuruse aastakäibega väikeettevõtetele mugava raamatupidamise äriregistri keskkonnas. Laiendame ettevõtjaportaali, lisades sinna mugava võimaluse käibemaksukohusluse ja kaubamärgi registreerimiseks;', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (36, 38, 'lihtsustame äriühingute ümberkujundamise ja lõpetamisega seotud reegleid, et soodustada ettevõtlusaktiivsust ja juhtimisoskuste taaskasutamist;', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (36, 39, 'viime läbi auditi Euroopa direktiividest tuleneva halduskoormuse minimeerimiseks Eesti seadusandluses.', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 40, '8. Ettevõtluse õpe ja suurem väärtustamine:', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (40, 41, 'väärtustame ettevõtjat ühiskonnas kui uue rikkuse loojat;', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (40, 42, 'algatame üliõpilastele suunatud programmi „Hakka ettevõtjaks!";', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (40, 43, 'viime ettevõtlusõppe põhihariduse õppekavasse ning tagame 2013. aastaks kõigile mittemajanduseriala tudengitele oma õppekava raames ettevõtlusõppe.', 5);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 44, '9. Energiajulgeoleku tagamine odavaima võimaliku hinnaga tarbijaile ja keskkonnale:', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (44, 45, 'tagame ettevõtetele ja tarbijatele odavaima võimaliku hinnaga elektrivarustuse, mitmekesistades ja hajutades energiatootmist, ergutades rohkem kasutama kodumaiseid taastuvaid energiaallikaid ning vähendades sõltuvust julgeolekuriske sisaldavast energia sisseostmisest kolmandatest riikidest.', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 46, '10. Eksporti ja ettevõtlust takistavate transpordi pudelikaelte kaotamine:', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (46, 47, 'arendame lennuühendusi eesmärgiga, et Tallinnast saab kõigisse suurematesse Euroopa ärikeskustesse lennata põhimõttel „hommikul sinna, õhtul tagasi";', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (46, 48, 'koostöös Poola, Läti, Leedu ja Euroopa Komisjoniga peame vajalikuks koostada ühiskava Baltimaade paremaks ja kiiremaks ühendamiseks Lääne-Euroopaga maismaad pidi (Via Baltica, Rail Baltica, Tallinn-Varssavi raudteeühendus jm.) ning analüüsime Tallinn-Peterburi reisirongiliini taasavamise otstarbekust.', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 49, '11. Mugava äritegemise taristu edendamine:', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (49, 50, 'liidestame riiklikud põhiregistrid (maakataster ja kinnistusraamat). Arendame registrite ristkasutust eesmärgiga viia andmete esitamisega seotud koormus ettevõtja jaoks võimalikult väikeseks;', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (49, 51, 'katame Eesti ülikiire internetiühendusega; e-teenuste ja kogu IT sektori potentsiaali edasiarendamiseks ning ettevõtlusvõimaluste parandamiseks regioonides on oluline kaasaegse info- ja kommunikatsioonitehnoloogia taristu väljaarendamine;', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (49, 52, 'oleme eestvedajaks Euroopa digiteenuste ühisturu väljaarendamisel, ennekõike digitaalse isikutuvastamise rahvusvahelise süsteemi rajamisel.', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 53, '12. Teadmuspõhist loomemajandust toetava taristu edendamine:', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (53, 54, 'jätkame investeeringuid riigi ja juhtivate kõrgkoolide osalusel loodud teadusparkide taristu arendamisse, et viia kokku innovatsioon ja ettevõtted;', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (53, 55, 'toetame kunstide rolli suurenemist ettevõtluse mitmekesistamisel, sealhulgas disaini suuremat rakendamist tootearenduses. Muudame EASi toetusmeetmeid loomemajanduse eripäradele vastavaks;', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (53, 56, 'loome tingimused rahvusvaheliselt tunnustatud tippspetsialistide palkamiseks strateegilistele erialadele Eesti ülikoolides;', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (53, 57, 'kaasajastame intellektuaalse omandi õiguse.', 6);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 58, '13. Võitlus reguleeritud hindadest tuleneva hinnatõusuga:', 7);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (58, 59, 'loome konkurentsi gaasi pakkumises, et vähendada sõltuvust Vene gaasist ning suruda alla gaasi ja toasooja hind Eesti ettevõtjatele ja tarbijatele;', 7);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (58, 60, 'vähendame taastuvenergia toetusi, kahjustamata investeerimiskeskkonda ja riigi poolt endale seatud keskkonnaeesmärkide saavutamist.', 7);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 61, '14. Riigivara tootlikkuse suurendamine:', 7);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (61, 62, 'viime avaliku sektori jaoks mittevajaliku vara tsiviilkäibesse. Kaalume kapitalituru normaalseks toimimiseks valitud riigiettevõtete osaluse noteerimist börsil. Lõpetame maa- ja omandireformi;', 7);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (61, 63, 'koondame alustava ettevõtluse riiklikud tugimeetmed ühtsesse, lihtsasse ja paindlikku programmi;', 7);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (61, 64, 'soodustamaks suuremahulisi investeeringuid, peame vajalikuks pikendada riigi poolt väljastatavate ressursilubade (vee erikasutusluba jne) kehtivuse tähtaegu.', 7);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 65, '16. Õigusriigi ja õigussüsteemi toimivuse ning ärikeskkonna parandamine:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (65, 66, 'lähtume mistahes ärikeskkonna muudatuste kavandamisel õiguskindluse ning vastava valdkonna ettevõtluse esindajate kaasamise põhimõttest;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (65, 67, 'arendame kohtusüsteemi sihiga muuta õigusemõistmine kodanikele kiiremaks ja odavamaks:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 68, 'kohtasjade menetlus ei tohi üheski kohtuastmes üldreeglina kesta üle 100 päeva. Loome uusi võimalusi kohtule alternatiivsete menetluste rakendamiseks. Kohtuotsuse täitmine peab olema tagatud;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 69, 'täiustame maksejõuetusõigust ja täitemenetlust;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 70, 'lihtsustame ja muudame läbipaistvamaks detailplaneeringute menetlemise ja ehitusloa saamise, sealhulgas vähendame bürokraatiat kodu remontimise ja soojustamise kavandamisel;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 71, 'vaatame üle ärikeeldude kohaldamise regulatsiooni, eesmärgiga kõrvaldada turult ebaausad ettevõtjad;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 72, 'korruptsiooni ennetamiseks ja tõkestamiseks kasutame kõiki sotsiaalseid, õiguslikke ja poliitilisi abinõusid;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 73, 'vaatame kriitilise pilguga üle riigilõivud;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 74, 'rakendame täiendavaid abinõusid võitluses maksupettuste ja maksudest kõrvalehiilimisega;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 75, 'kaitsmaks paremini tarbijate huve muudame konkurentsiregulatsiooni tõhusamaks ja selgemaks. Suurendame Konkurentsiameti õigusi sekkuda olukordadesse, kus ettevõtted kuritarvitavad turupositsiooni või on vaba konkurents muul moel riivatud;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (67, 76, 'algatame EASis projekti, et teha äritegevuseks tarvilik seadusandlus inglise keeles kättesaadavaks.', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 77, '17. Ettevõtlustoetuste kasuteguri ning laenude ja garantiide osa suurendamine ekspordi toetamisel:', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (77, 78, 'maksumaksja rahast makstavatest ettevõtlustoetustest on kasu vaid siis, kui need parandavad Eesti majandusstruktuuri, mistõttu tuleb Arengufondi, EASi, Kredexi ja PRIA kaudu makstavate toetuste maht ja teravik suunata ennekõike alustavate ja eksportivate ettevõtete toetuseks;', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (77, 79, 'innovaatiliste ja ambitsioonikate ettevõtete sünniks käivitame programmi Start-up Eesti;', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (77, 80, 'riigieelarve vahendite piiratuse juures peame õigeks järk-järgult liikuda toetustelt laenude ja tagatiste kasutamisele ning keskenduda koostööd ja tootearendust ergutavatele meetmetele;', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (77, 81, 'Eesti ettevõtete rahvusvahelistumise ergutamiseks analüüsime omakapitali (private equity) kättesaadavust ning vajadusel arendame edasi Sihtasutus Kredexi tooteportfelli ületamaks omakapitali pakkumisel esinevaid turutõrkeid.', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 82, '18. Investeeringute ligimeelitamine ja Eesti ettevõtluskeskkonna jõulisem tutvustamine välisturgudel:', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (82, 83, 'seame eesmärgiks muuta Eesti rahvusvaheliste äride peakorteriks, regionaalseks meditsiini-, küberkaitse- ja messikeskuseks, tunnustatud loomemajanduse ja disainimaaks;', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (82, 84, 'kaalume õigusliku raamistiku loomist pikaajaliste partnerluslepingute sõlmimiseks riigi ja Eestisse suuremahulisi välisinvesteeringuid toovate ettevõtete vahel;', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (82, 85, 'aktiviseerime välisteenistuse toetust Eesti ettevõtetele välisturgudele sisenemisel ja tegutsemisel;', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (82, 86, 'algatame eraldi programmi Aasia kapitali ja turistide meelitamiseks Eestisse.', 9);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 87, '19. Finantskeskkonna turvalisuse suurendamine:', 10);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (87, 88, 'ausa konkurentsi kindlustamiseks tugevdame järelevalvet panganduse ning pensionifondide tegevuse üle;', 10);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (87, 89, 'lihtsustame reegleid, et sisuliselt käivituks börs alternatiivse heade äriideede rahastamise allikana ka väike- ja keskmise suurusega ettevõtetele. Eesti elanikest peab saama omanikuühiskond ka ettevõtete omanikeks olemise kaudu.', 10);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (5, 90, '20. Kindlustusturu edasiarendmine:', 10);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (90, 91, 'tegutseme sihiga laiendada kindlustustegevust oluliselt enam ka tervisest ning ajutisest või alalisest töövõimetusest ja maksejõuetusest tulenevatele riskidele.', 10);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (92, 'Valitsusliidu pere- ja rahvastikupoliitika eesmärk on luua Eestist peresõbralik riik, kus inimesed soovivad hea meelega lapsi saada ja kasvatada ning väärikalt vananeda, mis kindlustaks, et Eesti rahvast saab kasvav rahvas. Valitsusliit soovib, et Eestis sünniks lapsi veelgi rohkem. Eesti olgu parim paik elamiseks. Valitsusliit peab hooliva ja peresõbraliku ühiskonna kujundamist parimaks viisiks, mis soodustab võõrsil töötavate ja elavate Eesti juurtega haritud inimeste kodumaale naasmist. Perekeskse hea ja turvalise elukeskkonna loomiseks peab Valitsusliit oluliseks läbi viia poliitikat ja struktuurseid reforme, mis lähtuvad järgmistest põhimõtetest:', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 93, '1. Toetava keskkonna edasiarendamine sündivuse suurenemiseks:', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (93, 94, 'tagame vanemahüvitise süsteemi jätkumise – vanemahüvitis on üles ehitatud pere- ja tööelu ühitamise ja tasakaalu põhimõttel, mille muutmine ühes või teises suunas viib sündide vältimiseni ja emade olukorra halvenemiseni tööturul;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (93, 95, 'toetame raseduskriisi aegset nõustamist, eesmärgiga vähendada abortide arvu;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (93, 96, 'kehtestame 1. jaanuarist 2013 vanemapensioni – maksame 01.01.2013 ja hiljem sündinud lapse ühele vanemale lapse kasvatamise eest 4% riigi keskmiselt sotsiaalmaksuga maksustatavalt töötasult vanema II pensionisambasse kuni lapse 3-aastaseks saamiseni (välja arvatud vanema tööl käimise aeg). Tagamaks põlvkondade vaheline solidaarsus, maksame 01.01.1991-31.12.2012 sündinud lapse ühele vanemale pensionilisa kahe aastahinde väärtuses. 1. jaanuarist 2015 maksame enne 01.01.2013 sündinud lapse ühele vanemale täiendavat pensionilisa ühe aastahinde väärtuses.', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 97, '2. Kindlustunde tagamine peredele lapse kasvatamiseks ka pärast vanemahüvitise perioodi lõppemist:', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (97, 98, 'peretoetuste maksmisel peab edaspidi kehtima põhimõte, et toetatakse rohkem neid, kes reaalselt rohkem abi vajavad ja kel on rohkem lapsi;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (97, 99, 'perede aitamisel eelistame mõistlikku tasakaalustatud süsteemi teenuste pakkumise, universaaltoetuste ja otseste rahaliste toetuste vahel;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (97, 100, 'jätkame lasterikaste perede kodutoetuse programmi, majanduslike võimaluste tekkimisel kehtestame tugisüsteemi lasterikastele peredele ja üksikvanematele;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (97, 101, 'peame õigeks, et ühe pere lastel on õigus käia samas lasteaias;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (97, 102, 'koondame omavalitsuste infokeskkonnad ühtsesse lapsehoiu infosüsteemi, et lapsevanematel oleks mugav ja lihtne leida sobiv lastehoiuvõimalus;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (97, 103, 'jätkame koolilõuna riiklikku toetamist põhikoolis, väärtustame tervislikku ja värsket, kohapeal kohalikust toorainest valmistatud toitu.', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 104, '4. Pere- ja tööelu ühitamise võimaluste edasiarendamine:', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (104, 105, 'jätkame lapsehoiuvõimaluste paindlikumaks muutmist, et aidata vanemad kiiremini tagasi tööturule ja seeläbi suurendada perede sissetulekuid;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (104, 106, 'arendame lastehoiuteenuse kõrval ka teisi peredele suunatud teenuseid ning väärtustame peresõbralikkust ühiskonnas tervikuna. Peresõbralike ettevõtete riiklikuks tunnustamiseks kehtestame peresõbraliku ettevõtte tunnistuse – nii teab iga lapsevanem töökohta valides, kus tema väärtust hinnatakse ja vajadusi mõistetakse;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (104, 107, 'taastame isapuhkuse.', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 108, '5. Huvihariduse edendamine: noorte huvitegevuses osalemise suurendamiseks ja riskikäitumise vähendamiseks kehtestame 1. jaanuarist 2014 ringiraha.', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 109, '6. Kodussünnitamise täpsem seadustamine: oleme seisukohal, et pere soovi korral ja vastunäidustuste puudumisel võib naine valida sünnitamiseks ämmaemanda või naistearsti juuresolekul ka oma kodu või muu sünnitamiseks sobiva paiga.', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 110, '7. Tugi lastetutele peredele, kes soovivad lapsi:', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (110, 111, 'toetame lastetusravi, et tagada võrdne võimalus lapse saamiseks ka neile, kellel meditsiinilise sekkumiseta see võimalus puuduks;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (110, 112, 'vähendame lapsedamisega seotud bürokraatiat, et üha enam asenduskodudes elavaid lapsi jõuaksid senisest kiiremini sobivatesse peredesse.', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 113, '8. Laste õiguste tõhus kaitsmine:', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (113, 114, 'laste õiguste ja ka kohustuste paremaks reguleerimiseks kehtestame ja rakendame tänapäevase lastekaitseseaduse;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (113, 115, 'abi osutamise tõhustamiseks lapsele ja perele pöörame tähelepanu varajase märkamisele ja sekkumisele;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (113, 116, 'peame oluliseks peredele pakutavate sotsiaalteenuste kvaliteedi tõusu.', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (92, 117, '9. Vanemahariduse ja perenõustamise kättesaadavuse parandamine:', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (117, 118, 'soovime ühiskonnas rohkem väärtustada paremate peresuhete kujunemist, sest noorte eluhoiakute areng, sealhulgas ennasthävitava riskikäitumise vältimine on otseses seoses peresuhete püsivuse ja kvaliteediga;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (117, 119, 'soovime tõhusama nõustamise teel ennetada paarissuhete (nii abieluliste kui vabakooseluliste) purunemist ja toetada senisest enam vanemaharidust;', 11);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (117, 120, 'kooliprogramm peab senisest suuremat tähelepanu pöörama pereväärtustele ja riskikäitumise vältimisele. Toetame seksuaaltervise ning vanemluse alast haridust põhikoolis ja gümnaasiumiastmes.', 11);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (121, 'Eesti püsimise võti on haritud rahva käes, seetõttu on haridusvaldkond eelseisvate aastate prioriteet. Tahame viia Eesti hariduse Põhjamaade tipptasemele – meie eesmärk on haritud rahvas ja edukas Eesti.', 12);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (122, 'Hea haridus tähendab inimese jaoks suuremat palka, paremat kaitset töötuse vastu ja suuremat pensioni tulevikus. Võimalus saada maailmatasemel haridust annab peredele suurema kindlustunde laste saamisel. Parem haridus tagab tervislikuma ja õnnelikuma elu. Valitsusliit soovib ehitada jõukat Eestit, mis on Läänemere regioonis hästi lõimitud, kõrgtehnoloogiale tuginev ning ekspordile orienteeruv suurte investeeringute sihtriik. Sellise Eestini jõudmiseks on möödapääsmatu jätkata haridussüsteemi ümberkorraldustega.', 12);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (123, 'Valitsusliit näeb Eesti tuleviku võtit heas ja kättesaadavas hariduses.', 12);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (124, 'Et pakkuda rahvale parimat, seame hariduskorralduses ja õppesisus järgmised sihid:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 125, '1. Igale lapsele lastehoiuteenuse ja kvaliteetse alushariduse omandamise võimalus:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (125, 126, 'seame eesmärgiks, et alushariduse omandaks koolieelsetes lasteasutustes ja koolide ettevalmistusklassides vähemalt 95% lastest. Jätkame lasteaiakohtade loomist ja lasteasutuste energiasäästlikkuse parandamist Euroopa Liidu struktuurifondide KOIT kava ning Kyoto saastekvootide müügist laekuvate vahendite toel. Vähendades erinevusi kooliminejate ettevalmistuses väldime hilisemat haridustee katkemist;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (125, 127, 'alus- ja põhihariduses peab keskenduma õppimisoskuse, iseseisva mõtlemise, loovuse ja õpijulguse kujundamisele. Õppe ja kasvatusalal on oluline väärtustel põhinev tegevus;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (125, 128, 'lapsekeskse lastehoiuteenuse rakendamiseks seame pikaajaliseks sihiks õpetajate (sh eripedagoogide) arvu suurendamise lasteaialapse kohta.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 129, '2. Kaasaegsem ja kvaliteetsem üldharidus:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (129, 130, 'rakendame uue õppekava, mis vähendab õpilaste õppekoormust põhikoolis ja annab neile selle kõrval rohkem võimalusi loominguliseks tegevuseks. Peame väärtuskasvatust koolielu lahutamatuks osaks;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (129, 131, 'kindlustame õpilastele koolides parimad tingimused arenemiseks. Peame oluliseks õppekeskkonna parandamist loodusainetes, matemaatikas, tehnoloogia- ja teadusõppes;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (129, 132, 'õppetöö mitmekesistamiseks ja haridusele parema ligipääsu tagamiseks, võtame kasutusele seda lihtsustavaid uusi tehnoloogiaid. Laiendame e-õppe võimalusi, et kergendada koolikotti ja vanemate osalust õpiprotsessis. Loome e-gümnaasiumi, kuhu koondame akadeemilise õppevara ja kust nii koolid kui õpilased saavad kursusi alla laadida;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (129, 133, 'soodustame lastevanemate suuremat osalust õppeprotsessis ning võimaldame koolide juures selleks vajalikku ettevalmistust;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (129, 134, 'viime miinimumini varase koolist väljalangevuse, ajutised kõrvalejääjad toome kooli tagasi;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (129, 135, 'soosime vabahariduslikku tegevust ja alternatiivpedagoogikate kasutamist, kui see annab osale õpilastest võimaluse õppida just neile sobivas keskkonnas.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 136, '3. Õpetajakutse väärtustamine, et õpetajateks saaksid parimad:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (136, 137, 'toimiva kutsevaliku ja korraliku, harjutuskoolidele tugineva õpetajakoolituse abil ülikoolides, saavutame, et õpetajaamet oleks ihaldusväärne meie parimatele ülikoolilõpetajatele;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (136, 138, 'jätkame 12 782 eurose õpetaja lähtetoetuse maksmist noortele alustavatele õpetajatele;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (136, 139, 'pakume õpetajakutse valikut aktiivselt juba alates gümnaasiumist, lähtudes „Noored kooli!" ja teiste sarnaste projektide kogemusest. Tagame õpetajatele kõrgetasemelise baas- ja täiendkoolituse. Õppekava kvaliteedi tagamiseks sätestame, et haridus- ja teadusministeerium saab õiguse välis- ja sisehindamise tulemuse alusel hinnata koolijuhtide pädevust.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 140, '4. Mõistliku koolivõrgu kujundamine:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (140, 141, 'keskendumine hariduse sisule ning omandivormide vabadus ja võrdsus on eelduseks Eestile sobivaima haridusvõrgu kujunemisel. Tagame õpilastele ka edaspidi võrdse juurdepääsu kvaliteetsele kooliharidusele;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (140, 142, 'toetame maakonnagümnaasiumide väljaarendamist kõigis maakonnakeskustes nn puhta gümnaasiumina, vajadusel riigigümnaasiumina;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (140, 143, 'pakume rahvusvahelistele tippspetsialistidele võimalust panna oma lapsed Euroopa Kooli ning saada rahvusvahelist bakalaureuseõpet Inglise Kolledžis ja Miina Härma Gümnaasiumis.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 144, '5. Vähemusrahvustest õpilaste edukas lõimimine kvaliteetset haridust andvasse haridussüsteemi, eesti keele õpe kõigile:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (144, 145, 'kindlustame tasemel eesti keele õppe muukeelsetes lasteaedades, põhikoolides ja kutsekoolides. Selleks laiendame keelekümblusprogrammi kasutamist;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (144, 146, 'viime lõpule vene gümnaasiumide ülemineku eesti õppekeelele. Aitame omavalitsustel moodustada tugevaid vene õpilaskonnaga, kuid hea eestikeelse õppega nn vene lütseume;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (144, 147, 'laiendame Ida-Virumaal eesti keeles õpetavatele õpetajatele 30% lisatasu maksmist;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (144, 148, 'pakume jätkuvalt tasuta eesti keele õppimise võimalusi;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (144, 149, 'laiendame eesti keele õppimise võimalusi teiste riikide haridusasutustes;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (144, 150, 'kindlustame vähemuskeelsetes põhikoolides kõrgel tasemel eesti keele, kultuuri, ajaloo ja ühiskonnaõpetuse õpetamise.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 151, '6. Kutseõppe kui haridusvaliku taseme ja populaarsuse tõstmine:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (151, 152, 'ehitame kaasaegsel tasemel välja kõigi kutsekoolide õppekompleksid ja ühiselamud;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (151, 153, 'Kutsekoolidest peavad saama kohalikud kompetentsikeskused, millel on laiem roll kogu piirkonna arendamisel;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (151, 154, 'loome kutsekoolides võimaluse täiendavaks õpilaste vastuvõtuks;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (151, 155, 'soodustame üld- ja kutsehariduslike õppeasutuste koostööd ning enesetäiendusvõimalusi kutsehariduse läbinutele, kes soovivad jätkata haridusteed kõrgkoolis. Selleks võimaldame kutsekooli õpilastel omandada soovi korral üldaineid gümnaasiumi omadega sarnases mahus;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (151, 156, 'kutsekoolilõpetaja peab kooli lõpuks suutma omandada tööandjaid rahuldava kutsekvalifikatsiooni.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 157, '7. Elukestvas õppes osalemise suurendamine:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (157, 158, 'seame elukestva õppe projektide rahastamise Euroopa Sotsiaalfondi vahendite kasutamise prioriteediks. Lisaks sellele peame oluliseks noorsootööprojektide rahastamist. Haridussüsteemi tagasipöördumine ei ole ühelegi inimesele takistatud või ülemäära keeruliseks muudetud;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (157, 159, 'elukestvas õppes osalevate täiskasvanute osakaalu suurendamiseks ühiskonnas tänaselt 12%-lt 20% tasemele ei käsitle kulutusi tööga seotud tasemekoolituselt erisoodustusena;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (157, 160, 'vanemate sissemaksed oma lapse haridusfondi või -kindlustusse saab jätkuvalt maha arvata isiku maksustatavast aastatulust.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 161, '8. Tasuta ja tipptasemel kõrgharidus:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (161, 162, 'seame kvaliteedi kõrghariduskorralduse läbivaks eesmärgiks. Soovime, et kõrghariduse kvaliteet oleks ühiskonna üldise tähelepanu ja avaliku debati keskmes, muuhulgas toimiks teadusuuringute kaudu Eesti haridusseisu järjepidev seire;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (161, 163, 'loome võimaluse saada tasuta kõrgharidust Eestis kõigile võimekatele noortele. Selleks võtame 2011. aastal vastu seaduse, millega suurendame aastatel 2012-2014 avalik-õiguslikele ülikoolidele või rakenduskõrgkoolidele riiklikku tellimust 40 % võrra, tagades sellega õppemaksuta õppe kuni 12 500 sisseastumistingimused täitvale üliõpilasele eestikeelsetel õppekavadel ühe erialadiplomi omandamiseks nominaalaja jooksul. Õppetöö kvaliteedi tõstmiseks muudame riikliku koolitustellimuse jaotust;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (161, 164, 'käivitame 1. jaanuarist 2015 vajaduspõhise õppetoetuste süsteemi ja tõstame õppeedukust arvestavaid riiklike stipendiume;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (161, 165, 'seisame selle eest, et kõrghariduse struktuuriuuenduseks saaks muuhulgas kasutada ka 2014. aastal algava Euroopa struktuurifondide uue rahastamisperioodi vahendeid;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (161, 166, 'loome magistrandidele suurema võimaluse panna kokku isikupärane õpingukava erinevate ülikoolide parimaist kursusist. Doktorandid hakkavad nooremteaduritena saama palka, mis on suurem seni makstud stipendiumist;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (161, 167, 'säilitame riiklikud toetused väikestele humanitaarteaduslikele erialadele ülikoolides. Arvestame humanitaarteaduste, sealhulgas ka rahvusteaduste erilaadsusega.', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (124, 168, '9. Kasvab ühiskonna panustamine teadus- ja arendustegevusse ning rahvusvahelisse teaduskoostöösse:', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (168, 169, 'seame sihiks jõuda teadus- ja arendustegevuse rahastamisel 3%-ni SKTst, sealhulgas erasektori osaluseni vähemalt 2/3 ulatuses sellest. Jätkame teaduse populariseerimist;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (168, 170, 'muudame teaduse rahastamise süsteemi selgemaks, st säilitame teadusasutuste baasfinantseerimise ja loome projektitoetuste jagamiseks kahe asutuse asemel Eesti Teadusagentuuri. Kehtestame teaduse rahastamisel teadlaste karjäärimudeli. Isikukeskne teaduse rahastamise süsteem on asendunud konkurentsikesksega – teadusasutuste rahastamine sõltub teadustulemustest, mitte olemasolevate teadlaste arvust. Soodustame igakülgselt Eesti teadlaste osalemist rahvusvaheliste teaduskeskuste töös, samuti ülikoolide, riigi ja erasektori koostööd ning ettevõtjate keskendumist tootearendusele;', 12);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (168, 171, 'jätkame teaduse ja kõrghariduse taristu välja arendamist.', 12);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (172, 'Valitsusliidu tööturu- ja sotsiaalpoliitika eesmärk on inimeste sissetulekute, elukvaliteedi ning sotsiaalse turvalisuse kasv. Väärtustame riigi hea majandusarenguga kaasnevaid kõrgemaid sissetulekuid, tegelikele vajadustele vastavaid inimväärseid toetusi ja väärikat vananemist.', 13);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (173, 'Selleks, et jätkuks inimeste sissetulekute kasv, paraneks elukvaliteet ja sotsiaalne turvalisus, viib Valitsusliit ellu poliitikaid ja struktuurseid reforme, mis lähtuvad järgmistest põhimõtetest:', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (173, 174, '1. Stabiilne pensionitõus:', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (174, 175, 'pensionitõusu eelduseks on aruka ja konservatiivse majandus- ja rahanduspoliitika jätkamine, mis kindlustab majanduskasvu ning uued töökohad;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (174, 176, 'väärtustame sotsiaalselt aktiivset elu, mis tagab pensionile jäädes suuremad sissetulekud ning loob kõigile võimaluse töötada nii kaua kui ise soovitakse.', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (173, 177, '2. Põhitähelepanu noorte ning pikka aega tööturult eemal olnute tööpuuduse vähendamisele:', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (177, 178, 'inimeste parim võimalus tööturul edukas olla seisneb heas hariduses. Seetõttu pöörame enim tähelepanu inimeste hariduse ja kutseoskuste tõstmisele, mis võimaldavad neil tööturul taotleda paremaid tingimusi ja palka;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (177, 179, 'jätkame TULE ja KUTSE programmidega, et haridustee kõrg- või kutsehariduses katkestanud saaksid tööturult tagasi pöörduda hariduse omandamise juurde;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (177, 180, 'jätkame ja arendame tööturu olukorrast sõltuvalt aktiivseid tööturumeetmeid, muuhulgas palgatoetust, tööpraktikat, ettevõtluse alustamise toetust, erialase ettevalmistuseta inimeste koolitamist jmt;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (177, 181, 'parandame töötukassa võimet aidata inimesi uue töökoha leidmisel, pakkudes kiiremat teenust, tänapäevaseid IT-võimalusi ning personaalsemat lähenemist;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (177, 182, 'toetame pikka aega lastega kodus olnud emade tööturule tagasipöördumist;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (177, 183, 'tagame noortele karjääriõppe –ja nõustamise üldharidus- , kutse- ja kõrghariduse tasemel;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (177, 184, 'Ida-Virumaa pikaajalise töötuse leevendamiseks pakume EAS-i kaudu individuaalseid suuremahuliste tööstusinvesteeringute toetamise pakette, mis aitaksid luua uusi töökohti regioonis.', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (173, 185, '3. Elukestva õppe ja töötute ümberõppe vahendite suurendamine:', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (185, 186, 'tõstame elukestvasõppes osalemist 20%ni ning ümberõppes osalevate madalama haridusega isikute arvu;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (185, 187, 'jätkame riigi toel kutseõppe programmi 16-29 aastastele põhiharidusega või põhihariduseta töötutele. Toetame Euroopa Sotsiaalfondi pilootprojekti ülemaalist rakendamist, et anda täiskasvanutele tööalast koolitust kutseõppeasutustes.', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (173, 188, '4. Puuetega inimeste elukvaliteedi parandamine:', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (188, 189, 'hindame kodanikuühiskonna väärtust ja peame vajalikuks puuetega inimeste esindusorganisatsioonide suuremat kaasamist riiklikku otsustusprotsessi;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (188, 190, 'parandame puuete määramise süsteemi korraldust ning vähendame rehabilitatsiooniga seonduvat bürokraatiat;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (188, 191, 'algatame puuetega inimeste tööhõive programme. Viime läbi Töötukassa tööturumeetmete revisjoni, et need soodustaksid puudega inimese töösuhet ja oleksid paindlikud. Kaardistame puude liikide kaupa valdkonnad, kuidas laiemalt rakendada ja koolitada inimesi;', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (188, 192, 'seame eesmärgiks ratifitseerida ÜRO Puuetega Inimeste Konventsioon.', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (173, 193, '5. Investeeringute suurendamine hoolekandeasutustesse: riigi poolt hallatavate erihooldeasutuste ja asenduskodude renoveerimesel lähtume põhimõttest, et ehitatakse tänapäevased peremaja tüüpi külad, kus abivajajatele saab pakkuda parimat hoolitsust.', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (173, 194, '6. Igale vajajale võimalikult võrdväärse abi tagamine: kehtestame riiklikult loetelu minimaalselt vajalikest sotsiaalteenustest ja nende kvaliteedinõuded (standardi), mida iga omavalitsus peab inimestele pakkuma.', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (173, 195, '7. Võimaluste loomine paindlikumaks ja kvaliteetsemaks elukorralduseks pensionipõlves:', 13);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (195, 196, 'edendame koduhooldust ja telemeditsiini ning loome täiendavaid võimalusi eakate kvaliteetseks päevahoiuks.', 13);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (197, 'Valitsusliidu tervishoiupoliitika eesmärk on Eesti inimeste pikem ja tervem elu ning kvaliteetne arstiabi terviseprobleemide korral. Valitsusliidu silmis on eluea üldisest pikkusest veelgi olulisem just tervena elatud aastate arv, mis on viimase viie aastaga kasvanud rohkem kui viie aasta võrra.', 14);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (198, 'Nüüdismeditsiin suudab pakkuda kvaliteetset abi, kui inimene seda vajab, aga inimestel endil on võimalus väga palju teha oma tervise säilimise või parandamise eesmärgil. Tervislik toitumine ja piisav kehaline aktiivsus on hea tervise alused.', 14);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (199, 'Eestis pakutav tervishoiuteenus on Euroopa võrdluses heal tasemel, kuid sellele vaatamata soovib Valitsusliit astuda samme arstiabi veelgi paremaks muutmiseks.', 14);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (200, 'Valitsusliidu nägemuses on parem rahvatervis ja tervishoid saavutatav, kui viia ellu järgmistest põhimõtetest lähtuvad poliitikad ja struktuursed reformid:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 201, '1. Inimeste eeskujuliku tervisekäitumise toetamine:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (201, 202, 'edendame tervislikke eluviise, kaasajastame Rahvatervise seaduse. Tunnustame riiklikult terviseedendajaid;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (201, 203, 'suurendame korrapäraste tervisekontrollide mahtu nii lastele kui täiskasvanutele, samuti erinevate tervisejälgimis- ja sõelumisprogrammide läbiviimist;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (201, 204, 'Eesti tippkokki kaasates julgustame Eesti inimesi juba lasteaiast ja koolist alates tervislikult toituma, kasutama toidu valmistamisel kodumaiseid ja mahetoidu tooraineid ning tõstma üldist toitumiskultuuri;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (201, 205, 'seame eesmärgiks vähendada vigastussurmade hulka vähemalt Euroopa keskmise tasemeni, rakendades pidevat erinevate valdkondade vigastussurmade ennetamise poliitikat.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 206, '2. Igaüks peab saama valida talle sobivaima tervishoiuteenuse:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (206, 207, 'toetame patsientide vaba liikumist, võtame initsiatiivi regionaalses koostöös vaba liikumise rakendamisel ning arendame Eesti patsientide vaba liikumist toetavate infosüsteeme;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (206, 208, 'selleks, et avardada Eesti-siseselt patsientide valikuvõimalusi raviteenuste saamiseks peame õigeks ravi pakkuvate teenuseosutajate paljusust ja raviks ettenähtud rahaliste vahendite suuremat sidumist patsientidega;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (206, 209, 'tervishoiuteenuse tagamiseks kõikjal Eestis loome erasektoriga koostöös asendusperearstide süsteemi, mis tagaks perearsti olemasolu igale elanikule ka siis, kui oma perearst puhkab või tekib muu ettenägematu takistus teenuse osutamiseks raskesti ligipääsetavates paikades.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 210, '3. Ravimid on odavamad ja paremini kättesaadavad:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (210, 211, 'arendame koostööd nii Euroopa Liidu kui muude riikidega, et tagada suurem ravimite valik Eestis;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (210, 212, 'tugevdame riigi võimet pidada läbirääkimisi ravimite hinna kujundamisel ja järelevalve teostamisel;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (210, 213, 'kaasajastame ravimipoliitika. Ravimite kättesaadavuse parandamiseks ja omaosaluse vähendamiseks analüüsime kehtivaid piiranguid. Kaalume apteegibussi teenuse sisseseadmist ning digiretsepti süsteemi loodud eelduste ärakasutamiseks seadustame ravimite ostmise võimaluse traditsioonilise apteegi osana toimiva e-apteegi kaudu.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 214, '4. Tervishoiu rahastamise mitmekesistamine ja tõstmine rahakasutuse parema sihituse teel:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (214, 215, 'analüüsime ja kaalume tänase süsteemi muutmist selliselt, et inimeste ravikindlustuseks mõeldud raha läheks valdavalt raviteenuste rahastamiseks;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (214, 216, 'toetame tervishoiu rahastamise mitmekesistamist, suurendades võimalusi vabatahtlikuks tervisekindlustuseks;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (214, 217, 'toetame perearstitöö kvaliteedi parandamisele suunatud lisatasusüsteemi rakendamist võimaldamaks perearstidel palgata lisaõde, et muuta tõhusamaks perearstide tegevus haiguste ennetamisel ja krooniliste haigete jälgimisel.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 218, '5. Arstiabi korralduse ajakohastamine:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (218, 219, 'uuendame nii riikliku südamestrateegia kui ka vähistrateegia, sealhulgas rajame võrgustikupõhise vähiravisüsteemi;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (218, 220, 'anname perearstidele suuremad võimalused inimeste tervisliku käitumise suunamisel, suurendame noorte arstide riiklikku tellimust ning parandame arstiabi kvaliteeti;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (218, 221, 'toetamaks maakonnakeskuste haiglatesse noorte arstide tööleasumist, maksame noorele arstile esimese residentuuri lõpetamise järgselt tööle asumisel kohalikku või üldhaiglasse 15000 euro suurust lähtetoetust;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (218, 222, 'rajame Tallinnas laste vaimse tervise keskuse.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 223, '6. Eakatele suunatud teenuste arendamine: tagame eakatele paremad hooldusravi ja koduõenduse võimalused. Demograafilisest seisundist tulenevalt eelistame rahastada hooldusravi ennaktempos ning rajame igasse maakonda kaasaegsed hooldusravikeskused.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 224, '7. Patsientide teadlikkuse tõstmine:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (224, 225, 'peame õigeks, et patsiendiga seotud ravijuhu maksumus oleks patsiendile nähtav riigiportaali kaudu;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (224, 226, 'suurendame patsienditeadlikkuse tõstmisega (nagu näiteks koopia- ja originaalravimite võrdlemine) seotud programmide mahtu;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (224, 227, 'peame vajalikuks kiirelt rakendada terviseteadlikkuse õpe uue riikliku õppekava mahus. Esmaabi ja elustamise põhiteadmised peavad olema selgeks saanud juba põhikoolis.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 228, '8. Tõhus võitlus ühiskonda hävitavate nakkushaiguste, alkoholismi, tubaka ja narkomaaniaga:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (228, 229, 'pöörame eelkõige tähelepanu noorte riskiteadlikkuse tõstmisele;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (228, 230, 'HIV-nakkusega võitlemisel on riigi prioriteet uute nakatunute arvu vähendamine, pöörates erilist tähelepanu noorte ennetusmeetmetele;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (228, 231, 'narkomaanidega tegelemisel kasutame lisaks olemasolevatele ravimeetoditele senisest enam personaalsemat lähenemist ja vajadusel narkomaania võõrutusravi alternatiivseid meetodeid;', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (228, 232, 'alkoholi ja tubaka tarvitamise vähendamiseks vaatame üle riikliku alkoholi- ja tubakapoliitika ja karmistame karistusi nende alaealistele kättesaadavaks tegemise eest. Arendame välja alkoholi liigtarvitamise varajase avastamise ja nõustamise süsteemi, et ennetada kroonilise alkoholisõltuvuse väljakujunemist ja tagada ravi ning rehabiitatsiooniteenused motiveeritud alkoholist loobujatele.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 233, '9. Kõikide saarteelanike kindlustamine kvaliteetse arstiabiga: lisaks perearstiteenuse tagamisele kindlustab riik kõikide kõrgema tähtsusega väljakutsete puhul saartele helikopteriteenuse olemasolu.', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (200, 234, '10. Tervishoiuteenuste ekspordi ja meditsiiniuuenduste edendamine:', 14);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (234, 235, 'peame vajalikuks Eesti meditsiini- ja infokommunikatsioonitehnoloogia oskusteabe tugevamat ühendamist riigi ja juhtivate ülikoolide poolt loodud teadusparkides, mis aitab viia Eesti meditsiiniinnovatsiooni uuele tasemele. Riik saab seda toetada investeeringutega teadusparkide taristusse ning EASi toel Eesti meditsiiniuuenduste rahvusvahelist turundustegevust koordineerides.', 14);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (236, 'Valitsusliidu energiapoliitika eesmärk on Eesti energiajulgeoleku kindlustamine odavaima võimaliku hinnaga Eesti tarbijatele ja keskkonnale. Kindel, taskukohane ja puhas energiavarustus tugevdab Eesti ettevõtete konkurentsivõimet ning loob eeldused suuremaks majanduskasvuks ja uute töökohtade tekkeks. Energia säästmine ja tõhusam kasutamine on olulised vahendid energiajulgeoleku saavutamisel odavaima võimaliku hinnaga.', 15);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (237, 'Valitsusliit näeb energeetikat Eesti arengu ühe võtmevaldkonnana mitte üksnes julgeoleku, vaid ka Eesti ekspordivõime kasvatamise seisukohalt. Energiast ja meie unikaalsest tehnoloogilisest oskusteabest energiatootmise vallas peab saama oluline Eesti ekspordiartikkel, majanduse, töökohtade ja sissetulekute kasvu allikas.', 15);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (238, 'Nende eesmärkide saavutamiseks soovib Valitsusliit läbi viia poliitikat ja struktuurseid reforme, mis lähtuvad järgmistest põhimõtetest:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 239, '1. Energiasõltumatuse hoidmine ka pärast 2016. aastat:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (239, 240, 'Eesti peab jõuliselt jätkama energiaallikate portfelli mitmekesistamist ning säilitama energiasõltumatuse tänasel määral ka pärast 2016 aastat, st et ülekaalukas osa Eestis tarbitavast energiast toodetakse kodumaistest energiaallikatest või Eestis;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (239, 241, 'valmistame ette tuumaenergia kasutuselevõtu eelduseks oleva seadusandluse ning viime läbi erapooletud tasuvusuuringud ja keskkonnamõju hinnangud Eesti oma tuumajaama rajamise otstarbekuse hindamiseks. Tuumatehnoloogia, mida Eesti riik lubab oma territooriumil kasutada, peab olema kaasaegne ja turvaline. Seetõttu lähtuma Euroopa Komisjoni soovitusest ja lubame üksnes kolmanda või sellest kõrgema põlvkonna tuumareaktori kasutamist. Eesti võimalik tuumaenergia programm peab sisaldama kohalike spetsialistide väljaõpet ning tagama maksimaalse ohutuse ja ausad turutingimused. Eesti võimalikus tuumajaamas saavad soodsate ja stabiilsete elektrihindade tagamiseks ning töökohtade loomiseks võimaluse osaluse omandamiseks ka Eesti tööstuslikud elektritarbijad;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (239, 242, 'näeme perspektiivi kodumaiste vedelkütuste tööstusel vähendamaks sõltuvust vedelkütuse impordist.', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 243, '2. Rahvuslikku julgeolekut suurendavate ühenduste ja taristute rajamine:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (243, 244, 'Eesti energiajulgeoleku suurendamiseks liidame Eesti elektrivõrgud Kesk-Euroopa sünkroonalaga;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (243, 245, 'arendame edasi ühendusi Põhjamaade ja Lätiga, esmajärjekorras ehitame valmis Estlink 2;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (243, 246, 'Eesti gaasivarustuskindluse suurendamiseks ja konkurentsi tekitamiseks gaasiturul viime läbi gaasivõrgu omandilise lahutamise;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (243, 247, 'Soome ja Balti riikide turgude sidumiseks üheks atraktiivseks gaasituruks käsitleme prioriteetse ülepiirilise energeetika taristuprojektina BalticConnectori ehitamise ettevalmistamist, milleks taotleme kaasrahastamist Euroopa Liidu uuest eelarveperspektiivist. Jätkame tööd LNG terminali Eestisse rajamise ettevalmistamiseks;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (243, 248, 'Eesti osaleb aktiivselt Balti energiaturu integratsiooniplaani (Baltic Energy Market Interconnection Plan) väljatöötamisel ja elluviimisel ning suurendab energiakoostööd Põhjamaadega;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (243, 249, 'muudame seadusandlust, et kiirendada suurte taristuprojektide puhul maakasutuse lubade menetlemist.', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 250, '3. Selgete elektrikaubanduse reeglite kehtestamine Euroopa Liidus kolmandate riikidega:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (250, 251, 'seisame kolmandatest riikidest pärineva elektrienergia importimisel Euroopa Liidu ühtsete reeglite kehtestamise eest, ennekõike keskkonnanõuete ja ausa hinnakonkurentsi osas.', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 252, '4. Kütte- ja elektriarveid vähendavate programmide rakendamine:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (252, 253, 'jätkame energiasäästuprogramme, sealhulgas kortermajade soojustamise programmi (võimalusel rakendades neid ka väikeelamutele), mille tulemusel Eesti inimesed ja ettevõtted saavad oluliselt vähendada oma kütte- ja elektriarveid ning elada keskkonnasõbralikumat elu. Suuname CO2 kvootidest riigile laekuva tulu energiasäästu investeeringuteks;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (252, 254, 'riik peab näitama eeskuju, rajades uued avaliku sektori hooned energiasäästlikena;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (252, 255, 'töötame välja meetmed, et rajatavad hooned vastaks parematele energiastandarditele.', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 256, '5. Kodumaiste väiksema keskkonnamõjuga energiaallikate eelistamine majanduse konkurentsivõimet kahjustamata:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (256, 257, 'peame oluliseks suurendada Eesti energiatootmise ja -tarbimise portfellis kodumaiste taastuvate energiaallikate ning vähendada suure süsinikujäljega energiaallikate osakaalu;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (256, 258, 'räägime läbi tootjatega taastuvenergia toetuste määra vähendamiseks olemasolevate projektide osas, pidades silmas õiguskindluse põhimõtet, Eesti energiapoliitika eesmärke ning võimalikult väikest mõju tarbija rahakotile. Vaatame üle taastuvenergia toetuste määra uutele projektidele, diferentseerime need energiaallikate lõikes ning seame sõltuvusse elektri turuhinnast;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (256, 259, 'transpordist lähtuva saaste vähendamiseks soodustame mittefossiilsete kütuste kasutamist ning lokaalse saaste piiramiseks loome vastavate Euroopa Liidu standardite kehtestamisel elektriautode laadimiskohad suuremates Eesti keskustes;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (256, 260, 'soojusenergia tootmisel eelistame tõhusat koostootmist;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (256, 261, 'Eesti peab aktiivselt osalema Euroopa Liidu süsinikpoliitika kujundmisel ja rohesertifikaatide turul. Energiasertifikaatide turu käivitumine võimaldab Eestil jätkata energiaportfelli mitmekesistamist taastuvenergia allikatega, kahjustamata samas Eesti tarbijate rahakotti ja ettevõtete konkurentsivõimet.', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 262, '6. Julgeoleku tagamiseks vajalike energiatootmisvõimsuste rajamine odavaima võimaliku hinnaga tarbijale ja keskkonnale:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (262, 263, 'energiatootmisvõimsuste kavandamisel eelistame energiaallikate mitmekesisust ja pikaajalist konkurentsivõimet, sealhulgas Euroopa keskkonnapoliitika valguses.', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 264, '7. Põlevkivist Eesti jaoks saadava väärtuse oluline kasvatamine:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (264, 265, 'eelistame meie olulisima maavara suuremat väärindamist, sealhulgas põlevkiviõlitööstuse arendamist ning Eesti unikaalsete põlevkivialaste tehnoloogiliste teadmiste ja oskuste eksporti;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (264, 266, 'kaalume põlevkiviõli tootmisel põlevkivi riigitulu (royalty) põhimõtte rakendamise otstarbekust. Põlevkivi riigitulu suurus sõltub nafta maailmaturuhinnast ning arvestab tootjaettevõtte pikaajalisi äririske.', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (238, 267, '8. Energiatõhususe suurendamine läbi teadamispõhise energeetika ja targa elektrienergiakasutuse (nutivõrgu) edendamise:', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (267, 268, 'peame oluliseks teadmispõhise majanduse edendamist energeetikas, tehnoloogiliste lahenduste arendamist ja oskusteabe eksporti (nt põlevkivienergeetikas). Jätkame energiatehnoloogia programmi;', 15);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (267, 269, 'väärtustame intelligentsete elektrisüsteemide arendamist, mille tulemusel suureneb elektrienergia varustuskindlus ja elektrienergia kasutuse efektiivsus ning väheneb keskkonna saastamine.', 15);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (270, 'Valitsusliidu transpordipoliitika eesmärk on kindlustada inimeste ja kaupade vaba, mugav, kiire ja taskukohane liikumine Eestis ning Eesti ja muu maailma vahel. Hästi korraldatud transport parandab inimeste heaolu ja tõstab majanduse võistlusvõimet. Inimeste vaba liikumine pole mõeldav ilma transpordivõrgustike ja -vahenditeta. Teede ja tänavate võrk, nende kvaliteet ja korrashoid on Eesti-sisese liikluse jaoks võtmetähtsusega. Eesti inimeste vaba liikumise kindlustamisel ja majanduskasvu soodustava transpordipoliitika rakendamisel lähtub Valitsusliit järgmistest põhimõtetest:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 271, '1. Põhjamaade tasemel teedevõrgu väljaehitamine:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (271, 272, 'kehtestame teedeehitusnõuded, mis kindlustavad meie teed kasutusmugavuselt ja vastupidavuselt Põhjamaade tasemel teekattega. Ajakohastame talviseid teehoiunõudeid ja tõstame teehoolde võimekust erioludes;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (271, 273, 'jätkame riigi kruusateede katmist tolmuvaba kattega, kasutades Põhjamaade tehnoloogiaid. Koostame ja rakendame riikliku programmi "Eesti teed tolmuvabaks aastaks 2030";', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (271, 274, 'jätkame Tallinna ja Tartu ringtee, Tallinn-Tartu maantee neljarealiseks ehitamist, Saaremaa püsiühenduse ja Via Baltica rajamist ning Tallinn-Narva maantee rekonstrueerimist;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (271, 275, 'jätkame riigimaanteede hooldamist ja remonti pikaajalise riikliku teedehoiukava alusel.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 276, '2. Ühtse ja kasutajale mugava elektroonilise piletisüsteemi loomine: Eesti rakendab liikluse korraldamisel kõiki moodsaid infotehnoloogilisi vahendeid. Loome reisijatele mugava võimaluse teabe saamiseks ja piletite ostmiseks mistahes Eesti piires liikuvale ühistranspordivahendile ühest kesksest veebikeskkonnast.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 277, '3. Ühistranspordi arendamise kõrval jääb Eestis liiklemisel väga oluliseks maanteetransport:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (277, 278, 'Eesti hõreda asustuse ja traditsioonide tõttu jääb Eesti perede peamiseks liikumisviisiks maanteetransport. Sõiduautosid ei maksustata;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (277, 279, 'tõstmaks maanteetranspordi tõhusust kaubaveol peame oluliseks koostada uuringutele tuginev kava selleks, et vähendada Eesti põhilistel kaubaveotrassidel veokite massipiirangut.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 280, '4. Kõrvuti autoliikluse parandamisega teeb Eesti valitsus kõik selleks, et vähendada autode kasutamise sundi:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (280, 281, 'arendame kvaliteetset ühiskondlikku transporti nii maal kui linnas, et oleks tagatud piisav ühendus kõigi Eesti paikadega ning bussi- ja rongitranspordi parem haakuvus;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (280, 282, 'tõstame reisirongiliikluse tihedust, et maksimaalselt ära kasutada kiiremate rongide võimalusi;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (280, 283, 'mõjutame linna- ja elamuehitust selliselt, et vahemaad töö- ja elukohtade, kaupluste ja elukohtade, koolide ja lasteasutuse ning elukohtade vahel väheneksid;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (280, 284, 'osaleme Euroopa Liidu programmi „Tark linn/Smart City" loomisel. Valime linna, kus pilootprojektina toetame linnaplaneerimise põhimõtete juurutamist, mis vähendavad sunnitud liiklemise vajadust ja pakutakse elukohti, mille suhtes töökohad, teenindusasutused, lasteasutused on paigutatud võimalikult mugavalt. Ühtlasi on "Tark linn" ka energia kokkuhoiu ja keskkonnahoiu programm.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 285, '5. Liiklusohutuse suurendamine:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (285, 286, 'Eesti valitsus koostab Eesti rahvuslikku liiklusohutusprogrammi aastateks 2015-2025, et viia liiklusohutus Eestis Põhjamaade tasemele;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (285, 287, 'varustame põhimagistraalid vajalike barjääride, peale- ja mahasõitudega, et need oleksid läbitavad ühtlase kiirusega ning jätkame põhimagistraalide ohtlike lõikude ja ristmike valgustamist;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (285, 288, 'jalakäijate ja jalgratturite ohutuse suurendamiseks jätkame kergliiklusteede arendamist;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (285, 289, 'liiklusohutuse suurendamiseks on oluline roll liikluskasvatusel, teehoiul ja järelevalvel;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (285, 290, 'sujuva ja turvalise liiklemise kindlustamiseks rakendame aktiivselt EL Intelligentse Transpordisüsteemi (ITS) projekti tulemusi, muuhulgas infotehnoloogilisi ja sotsiaalmeedia lahendusi kodanike ohutuse ja liiklusmugavuse parandamiseks.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 291, '6. Eesti lennuühenduste oluline parandamine:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (291, 292, 'Eesti lennunduspoliitika lähim suur eesmärk on võimalus lennata Tallinnast kõigisse olulisematesse Euroopa ärikeskustesse ja tagasi ühe päeva jooksul;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (291, 293, 'Eesti kui sihtkoha reklaamimisel ühendame EASi, Tallinna Lennujaama ja lennukompaniide jõud. Pikaajalisemaks eesmärgiks on muuta Tallinna lennujaam Põhja-Euroopa Aasia-suunaliste lendude keskuseks;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (291, 294, 'Eesti osaleb aktiivselt Euroopa Ühtse Taeva projektis.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 295, '7. Kaubandussuhete arendamine meritsi ja maismaal:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (295, 296, 'mereriigina peab Eesti ära kasutama Tallinna Sadama eelised regioonis. Kavandame jäämurdevõimekuse suurendamist Euroopa Liidu uue eelarveperioodi vahendite toetusel. Meretranspordi, -turismi ja -hariduse edendamiseks kiidame Riigikogus heaks Eesti Merenduspoliitika. Laevade Eesti lipu alla registreerimine peab olema mugav, kiire ja vähese bürokraatiaga;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (295, 297, 'tõstatame Venemaa-poolsed piiriületusprobleemid Venemaaga peetavatel kõnelustel ja EL sellesisulistel foorumitel. Niisamuti peame oluliseks saavutada kokkulepe Venemaaga veokvootide suurendamiseks maismaatranspordis.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 298, '8. Kaasaegse maismaaühenduse rajamine Eesti ja Lääne-Euroopa vahel:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (298, 299, 'seame sihiks, et aastal 2014 saab uute rongidega Tallinnast Tartusse sõita alla kahe tunniga ning tiheneb sõidugraafik;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (298, 300, 'koostöös Poola, Läti, Leedu ja Euroopa Komisjoniga peame vajalikuks koostada ühiskava Baltimaade paremaks ja kiiremaks ühendamiseks Lääne-Euroopaga maismaad pidi (Via Baltica, Rail Baltica, sealhulgas Tallinn-Varssavi raudteeühendus jm.);', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (298, 301, 'analüüsime Tallinn-Peterburi reisirongiliini taasavamise otstarbekust.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 302, '9. Eesti-sisese veeliikluse edendamine ja väikesadamate arendamine:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (302, 303, 'konkurentsi tagamiseks praamide üleveoliinidel käsitleme Virtsu-Kuivastu ja Rohuküla-Heltermaa liine eraldiseisvatena. Kavandame riikliku tellimusena Rohuküla-Heltermaa liinile sobivate laevade soetamist Euroopa Liidu uue eelarveperioodi vahendite toel. Ehitame valmis laevad ühenduse pidamiseks Kihnu, Ruhnu, Abruka, Vormsi ja Prangliga;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (302, 304, 'siseturismi elavdamiseks ja uute töökohtade loomiseks investeerime EL vahendeid väikesadamate arendamiseks;', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (302, 305, 'kaalume ajaloolise siseveetee Peipsist Pärnu jõele taastamist ning EL koostööprogrammi abiga arendame Peipsi veeturismi võimalusi nii Soome kui Venemaa suunas.', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (270, 306, '10. Läänemere laevaliikluse turvalisuse suurendamine:', 16);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (306, 307, 'Jätkame aktiivset koostööd Helcom`iga (Helsinki Commission) Läänemere laevaliikluse turvalisemaks muutmiseks ja Läänemere reostatuse vähendamiseks.', 16);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (308, 'Valitsusliidu infoühiskonna poliitika eesmärk on pakkuda Eesti inimestele nende aega ja mugavust väärtustavat ning turvalist elu- ja töökeskkonda moodsate tehnoloogiavahenditega. Infoühiskonna keskmes peab olema i-nimene, mitte e-lektron. Tehnoloogia arendamine ei ole eesmärk omaette – riigi eesmärgiks on pakkuda inimestele paremaid teenuseid, kasutades selleks parimat võimalikku tehnoloogiat.', 17);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (309, 'Eesmärgi saavutamiseks viib Valitsusliit ellu poliitikat ja struktuurseid reforme, mis lähtuvad järgmistest põhimõtetest:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 310, '1. Infoühiskond ülikiire interneti kujul mõistliku hinnaga igasse kodusse ja ettevõttesse:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (310, 311, 'internet on oluline osa kaasaegse ühiskonna taristust. Toetame uue põlvkonna fiiberoptilistel kaablitel põhineva ülikiire lairibaühenduse väljaehitamist kõikide Eesti maapiirkondadeni.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 312, '2. Riigi ühtne, lihtne ja sõbralik kodanike poole pööratud nägu internetis ja e-teenustes:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (312, 313, 'muudame e-teenused mugavateks ja inimsõbralikeks i-teenusteks, kujundades eesti.ee kodanikele käepäraseks ja ligitõmbavaks võrgu kaudu pakutavate avalike teenuste väravaks, võimaldamaks arukat ja otstarbekohast dialoogi riigi ja kodanike vahel;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (312, 314, 'loome e-Eesti versiooni nutitelefonidele ning tagame seeläbi mugavaima e-teenuste kättesaadavuse;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (312, 315, 'ID-kaart ja mobiilID peavad tagama turvalise ligipääsu igale Eestis pakutavale teenusele;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (312, 316, 'ministeeriumid saavad ühtse ülesehitusega veebilehed.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 317, '3. Emakeele ja rahvuskultuuri hoidmine, tutvustamine ja arendamine ka e-keskkonnas:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (317, 318, 'täiustame keelenõustamise ja keeleuuenduste e-keskkonda, jätkame tõlkesõnaraamatute digitaliseerimist koostöös Google`iga ja peame oluliseks jätkata emakeelsete programmide, häältuvastustarkvara ja muu keeletehnoloogia loomist;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (317, 319, 'arendame kultuurisuhtlusportaale ja jätkame rahvuskultuuri digitaliseerimist.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 320, '4. Turvalisem, säästlikum, mugavam, kiirem, läbipaistvam ja piirkonniti kättesaadavam avalik teenus:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (320, 321, 'teabeedastuse uute tehnoloogiate abil saame reformida avaliku halduse ja muuta riik kodanike ja ettevõtjate jaoks veelgi tõhusamaks;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (320, 322, 'arendame edasi ettevõtja-, maksuameti- ja ühistranspordiportaali, e-asjaajamist ning earhiveerimist;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (320, 323, 'kodanikele parima teenuse pakkumiseks viib riik avalikud e-teenused mobiilidesse ja kasutab digiTV aina laienevaid võimalusi;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (320, 324, 'koostöös erasektoriga arendame Eesti digikaardi mitmekülgseks avalikuks teenuseks, mille kaudu saab tulevikus muuhulgas toimuma ka liikluse reguleerimine;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (320, 325, 'nimetame riigikantselei juures ametisse riigi IT-poliitikat koordineeriva ametiisiku, koondame avalikku teenistust ja selle tugifunktsioone puudutava otsustamise rahandusministeeriumi.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 326, '5. Üksikisiku privaatsuse tagamine:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (326, 327, 'tagame e-teenuste turvalisuse ja isikuandmete kaitse. Oluline osa on selles teadlikkuse arendamisel infoühiskonna võimalustest ja ohtudest. Peame vajalikuks õpetada internetiturvalisuse ABCd ja internetiliikluse eeskirju algkoolist peale;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (326, 328, 'viime läbi lapsevanemate teadlikkuse tõstmise kampaaniaid ning propageerime turvavara kasutamist;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (326, 329, 'Eesti peab võtma aktiivse eestvedaja rolli rahvusvahelise õiguse arendamises, et tagada isikuõigused ja infovabadus internetikasutuses.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 330, '6. eÕpetuse edendamine:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (330, 331, 'laiendame e-kooli võimalusi, et kergendada koolikotti ja hõlbustada vanemate osalust õpiprotsessis;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (330, 332, 'loome e-gümnaasiumi, kuhu on koondatud akadeemiline õppevara ja kust nii koolid kui õpilased saavad kursusi alla laadida;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (330, 333, 'samm-sammult rakendame moodsat infotehnoloogiat õppevahendeina, et õppimine oleks rõõmus ja põnev ka tulevikus.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 334, '7. eDemokraatia arendamine kaasamise ja läbipaistvuse suurendamiseks:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (334, 335, 'e-kodanik on e-ametniku ja e-poliitiku kiireks ja tegusaks partneriks;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (334, 336, 'nutitelefoniga on võimalik valida juba 2013. aasta kohalikel valimistel.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 337, '8. Riigi e-varade andmine kodanike ja ettevõtete teenistusse:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (337, 338, 'teeme riigi ruumiandmed töödeldaval kujul avalikuks - see annab kodanikele ja ettevõtetele võimaluse ise luua riigi andmete põhjal otstarbekaid teenuseid;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (337, 339, 'läbipaistvuse ja kaasatuse suurendamiseks ning erasektori ergutamiseks uute rakenduste loomisele muudame avalikud ehk riigi ja kohalike omavalitsuste andmed masinloetavaks (machine-readable public data);', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (337, 340, 'seame sihiks, et era- ja avaliku sektori koostöös loodud andmekogud oleksid ettevõtetele ja eraisikutele arendamiseks kättesaadavad.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 341, '9. Eestist maailma juhtiva E-ID ja küberjulgeoleku lahenduste tootja kujundamine:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (341, 342, 'oleme eestvedajaks Euroopa digiteenuste ühisturu väljaarendamisel, ennekõike digitaalse isikutuvastamise rahvusvahelise süsteemi rajamisel ning küberjulgeoleku edendamisel;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (341, 343, 'btöötame välja õigusliku lahenduse riigi IKT teenuste ekspordi toetamiseks, et Eesti ettevõtjad saaksid ka riigi ja Euroopa Liidu tellimusel loodud IKT lahendusi turustada;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (341, 344, 'jätkame IT-ekspordiklastri tööd;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (341, 345, 'muudame IT-hariduse riiklikuks prioriteediks, suurendame riiklikku vastuvõttu IT-teenuste loomise ja arendamisega seotud erialadel ülikoolides ning IT erialade pearaha;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (341, 346, 'ühendame Eesti infotehnoloogilise kompetentsi ja lõimime selle loomemajandusega;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (341, 347, 'käivitame Tartu Ülikooli ja Tallinna Tehnikaülikooli koostöös IT-akadeemia ning muudame Eesti Infotehnoloogia Sihtasutuse IT koolituse koordinatsioonikeskuseks;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (341, 348, 'rakendame maakondades programmeerimise huviringid ja viime programmeerimise valikainena gümnaasiumidesse.', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (309, 349, '10. i-Eesti isikupärase näo ja positiivse rahvusvahelise maine loomine internetimaailmas:', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (349, 350, 'paneme vastutuse Eesti riigi kuvandi loomise, arendamise ja hoidmise eest veebikeskkonnas ühele konkreetsele ministeeriumile;', 17);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (349, 351, 'Eesti peab saama interneti-alase õigusloome eeskujuks maailmas.', 17);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (352, 'Valitsusliidu sisejulgeoleku- ja turvalisuspoliitika eesmärk on inimelude, inimeste tervise ja vara edukas kaitsmine ning Eesti põhiseadusliku korra, sisemise rahu ja stabiilsuse kindlustamine. Riik peab suutma tagada inimeste turvatunde parimal moel, et see annaks igaühele kindluse ennast arendades, lapsi kasvatades ja majanduslikku edu taotledes. põhiseaduslike õiguste kaitse, võimude omavaheline tasakaal ning kontrollitavus ja jõustruktuuride vastavus kodanikuühiskonna nõuetele on põhimõtted, millest me lähtume. Näeme Eesti riigikorralduse ja õiguskorra arengut eelkõige stabiilse ja paindliku täiustumisena, mitte põhjaliku ümberkorraldamisena. Dünaamilise reageerimise kõrval tehnoloogia arengule ja väliskeskkonna muutustele, tuleb väärtustena au sisse tõsta ka institutsionaalne stabiilsus ja õigusrahu ning -selgus.', 18);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (353, 'Nende eesmärkide saavutamiseks soovib Valitsusliit läbi viia poliitikat ja struktuurseid reforme, mis lähtuvad järgmistest põhimõtetest:', 18);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 354, '1. Turvalisus algab elukeskkonnast ja ühiskondlikest hoiakutest:', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (354, 355, 'kodanike turvatunne võrsub heast majanduslikust ja sotsiaalsest keskkonnast, saab tuge eeskujulikult arendatud avalikust taristust ja arukast elukeskkonna planeerimisest, toetub õigeaegsele ja piisavale informeeritusele ning kodanike endi hoiakutele ja osalusele. Toetame linnaplaneerimise ja arhitektuursete lahenduste kasutamist turvalise elukeskkonna kujundamisel;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (354, 356, 'ühiste väärtuste kinnistumine ühiskonnas ja nende järgimine hoiavad ära paljud riskid, kuriteod ja õnnetused ning muudavad turvalisuse tagamise sihipärasemaks ja rahva ootustele vastavaks. Turvaline on ühiskond, kus õiguskaitseorganitel on vähe põhjusi sekkuda, kuid kui see on vajalik, siis toimub see tõhusalt ja kiiresti;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (354, 357, 'suuname kuriteoennetuse eelkõige noortele, toetades neile mõeldud kuriteoennetusprojekte ning hoolitsedes, et ka piisava vanemliku hoolitsuseta noortel oleks sisukat tegevust;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (354, 358, 'peame oluliseks liiklusturvalisuse jätkuvat suurendamist ja liiklusjärelevalvet. Jätkame kiiruskaamerate paigaldamist maanteede riskikohtadesse ja suurendame läbi selle liiklusturvalisust suurematel maanteedel;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (354, 359, 'seame sihiks ohvriabi tugivõrgustiku laiendamist üle kogu Eesti.', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 360, '2. Isikute põhiõiguste kaitsmine:', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 361, 'turvalisus peab teenima kodanike heaolu. Kogukonna, piirkonna, rahvuse, riigi suveräänsusel on mõtet vaid siis, kui see põhineb inimese vabadusel – tema põhiõiguste ja privaatsuse kaitstusel;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 362, 'kanname hoolt selle eest, et politsei kutsealane haridus ja väljaõpe oleksid suunatud seaduse ja avaliku korra tagamisele minimaalse jõu rakendamisega ning et järgitaks eelkõige demokraatlikke ja eetilisi väärtusi ning inimõigusi;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 363, 'panustame isikuandmete kaitse ja üksikisiku privaatautonoomia tagamisse. Tugevdame Andmekaitse Inspektsiooni võimekust ja juriidilist positsiooni üksikisikute õiguste kaitsel;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 364, 'kodanike riigis ei tohi kodanik oma riiki karta. Teostame karistusõiguse läbiva analüüsi ja ühtlustame karistuspoliitika. Võimaldame süütegude heastamist mitmesuguste alternatiivsete karistusvormide (ühiskondlikult kasulik töö jmt) kaudu;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 365, 'muudame süütegude menetlemise kiiremaks ja arusaadavamaks ning karistuste vaidlustamise lihtsamaks;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 366, 'seame sihiks valmis ehitada uus Tallinna vangla, millega viime lõpuni vanglate kaasajastamise. Suurendame vangide tööhõivet;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 367, 'tagame tõhusa, seadustes kirjeldatud järelevalve jõuametkondade üle;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 368, 'kindlustame, et seadusega oleks sätestatud mõistlikud kriteeriumid ja aeg salastatud materjalide avalikustamiseks, kanname hoolt, et salajaste vahendite suhtes saaks rakendada eriauditeid;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (360, 369, 'rakendame organiseeritud ja majanduskuritegudes ning korruptsioonis süüdimõistetute suhtes senisest enam vara konfiskeerimist, andes eriasjade prokuröridele ja politseinikele võimaluse spetsialiseeruda kriminaaltulu jälitamisele.', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 370, '3. Kodanikuühiskonna ja riigi koostöö süvendamine turvalisuse tagamisel:', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (370, 371, 'arendame riigi ja kogukondade omavahelist vabatahtlikku head koostööd;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (370, 372, 'edendame naabrivalvet;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (370, 373, 'avaliku korra mõiste, samuti avaliku korra kaitse peab olema ühtsetel alustel mõõdetav ning hinnatav kogu riigis. Rakendame Korrakaitse seaduse;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (370, 374, 'tagame korrakaitseüksuste suurema nähtavuse ja jõudmise abivajajani, koolitades ja kaasates iseseisva pädevusega abipolitseinike. Toetame abipolitseinike tegevust;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (370, 375, 'toetame vabatahtlike päästjate tegevust.', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 376, '4. Õigusruumi selguse ja arusaadavuse suurendamine:', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (376, 377, 'jätkame Eesti õigussüsteemi korrastamist selles suurema kooskõla ja lihtsuse saavutamiseks ning ülereguleerimise vältimiseks;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (376, 378, 'jätkame era- ja avaliku õiguse kodifitseerimist valdkondades, kus see on otstarbekas ja vajalik – et kodanik leiaks kogu valdkonna seadused ühest tekstist ning saaks üheselt aru oma õigustest ja kohustustest. Lisaks töös olevale keskkonnaseadustikule võtame vastu valdkonnaseadusi koondava ehitus- ja planeerimisseadustiku;', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (376, 379, 'arendame eesti õiguskeelt.', 19);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 380, '5. Korrakaitse nähtavuse, reageerimise kiiruse ja tõhususe tõstmine:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (380, 381, 'kuritegevuse vastase võitluse prioriteedid lähtuvad ohuhinnangutest. Iga-aastased prioriteedid otsustatakse justiits- ja siseministri poolt järgmiseks eelarveaastaks;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (380, 382, 'kuritegevuse kiiremaks tõkestamiseks ja päästeteenuse parema kättesaadavuse tagamiseks jätkame investeerimist pääste-, politsei-, piirivalve- ja ekspertiisiasutuste tehnikasse;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (380, 383, 'tihendame rahvusvahelist õiguskaitsealast koostööd;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (380, 384, 'kindlustame kõrgema tähtsusega väljakutsete puhul helikopteriteenuse olemasolu;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (380, 385, 'parandame korrakaitsjate väljaõpet ning kaalume uue Sisekaitseakadeemia rajamist Ida-Virumaale.', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 386, '6. Õiguskaitse rakendumise kiiruse ja vältimatuse tagamine:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (386, 387, 'kohtumenetluse pikkus ei tohi reeglina ületada 100 päeva ühes kohtuastmes, selleks sätestame mõistlike menetlustähtaegade järgimise üle selgema järelevalve ja vastutuse;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (386, 388, 'arendame edasi riigi õigusabi süsteemi ja eelarvevõimaluste tekkel laiendame tasuta õigusabi kättesaadavust abivajajatele;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (386, 389, 'peame vajalikuks tugeva ühendhäirekeskuse loomist ning ühele hädaabinumbrile üleminekut 112.', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 390, '7. Võitlus vägivallaga lähisuhetes ja koolis:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (390, 391, 'jätkame võitlust lähisuhete vägivalla, sealhulgas koolivägivalla vähendamiseks ja tõkestamiseks;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (390, 392, 'eelarvevõimaluste piires suurendame turvalisusteenuseid osutavate kodanikeühenduste, muuhulgas naiste varjupaikade rahastamist.', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 393, '8. Korruptsiooni ennetamine ja tõkestamine:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (393, 394, 'KAPO uurimisõigus peab laienema kõigile korruptsioonijuhtumitele, mis ohustavad Eesti Vabariigi julgeolekut;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (393, 395, 'võtame vastu uue korruptsioonivastase seaduse, mis muu hulgas täpsustab ametiisikute mõistet ja muudab majandushuvide deklareerimise sisuliseks ja elektrooniliseks;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (393, 396, 'uuendame aastast 2013 korruptsioonivastast strateegiat „Aus riik". Uus strateegia näeb ette täiendavaid meetmed korruptsiooni ennetamiseks ja keskendub veelgi enam korruptsiooni tõkestamisele erasektoris ja kohalikes omavalitsustes.', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 397, '9. Kodanike ja ettevõtete turvalisuse tagamine küberruumis:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (397, 398, 'töötame välja küberruumi korrakaitseks vajalikud regulatsioonid, täiendame seadusandlust ja käivitame küberruumi kuriteoennetava tegevuse, sealhulgas koolituse;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (397, 399, 'täiustame küberkaitse strateegiat, jätkame selle elluviimist ning tugevdame sel eesmärgil koostööd eraettevõtete ja riigi vahel.', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 400, '10. Julgeolekuasutuste ajakohastamine ja julgeolekustrateegia pikaajaline planeerimine:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (400, 401, 'tõhustame luure- ja vastuluuresektori korraldust tervikuna ning planeerime selle arengut pikemalt;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (400, 402, 'koostame pikaajalise julgeolekustrateegia valdkondade kaupa, mis sätestab selged julgeolekualased riiklikud eesmärgid ja ootused;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (400, 403, 'korrastame eri jõuametkondade töötajate sotsiaalse kohtlemise.', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (353, 404, '11. Rahvusvahelise turvalisuskoostöö edendamine:', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (404, 405, 'tõhustame korrakaitselist tegevust piiriülese kuritegevuse tõkestamiseks. Kindlustame parema koostöö lähinaabrite politsei, tolli ja piirivalvega narko- ja inimkaubandusevastases tegevuses;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (404, 406, 'toetame karistuspoliitika-alase seadusandluse ühtlustamist oma naaberriikidega Euroopa Liidus;', 20);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (404, 407, 'arendame koostööd võimalike merereostuse ja suurõnnetuste ühiseks likvideerimiseks Läänemeres ning ühiseks terrorismivastaseks tegevuseks.', 20);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (408, 'Valitsusliidu kultuuripoliitika eesmärk on tagada eesti rahvuse, keele ja kultuuri kestmine läbi aegade. Selleks arendame kultuuripoliitikat, mis kujundab kultuuri loojale ja tarbijale vaba ja loomingulise keskkonna ning aitab seeläbi kaasa rahva ja riigi püsimisele. Olemasoleva ja toimiva kultuuriraamistiku hoidmine, traditsioonide ja pärandi säilitamine, uue ja avarama pilguga tulevikku vaatamine ning töö noortega on järgnevate aastate kultuuripoliitika olulisemad suunad. Globaliseerumine avaldab survet kõigile rahvuskultuuridele, eesti kultuuri missioon on rahvusvahelise massikultuuri tasakaalustamine hea ja eestimaisega. Valitsusliit seisab võrdsete võimaluste loomise eest ning tunnustab kõiki Eestis toimivaid kultuuriloojaid ja -vahendajaid, sealhulgas omandi- ja organisatsioonilisest vormist sõltumata.', 21);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (409, 'Professionaalse- ja harrastuskultuuri elujõulisuse tagamiseks kogu Eestis lähtub Valitsusliit järgmistest põhimõtetest:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 410, '1. Riigitoetuste suunamine ennekõike professionaalseks loometegevuseks:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (410, 411, 'Kultuurkapitali aluspõhimõtteid ei muudeta. Eelarvevõimaluste tekkel suurendame professionaalse loomingu ergutamiseks mõeldud toetusi;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (410, 412, 'võtame suuna sellele, et riigiasutuste tegelikud väljaminekud oleksid finantseeritud riigieelarvest ja Kultuurkapitali vahendid läheksid ennekõike loovisikutele ja erakollektiividele projekti- ja loomingutoetusteks;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (410, 413, 'analüüsime võimalust toetada jätkuvaid kultuuriprojekte pikemaajalise toetuskava alusel;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (410, 414, 'kaalume võimalust muuta loovisikute seadust selleks, et lahendada vabakutselistele tunnustatud loovisikutele ravikindlustusega seotud probleem.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 415, '2. Kultuurile mõeldud rahaeraldiste, sealhulgas Euroopa Liidu toetuste suurendamine:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (415, 416, 'seisame selle eest, et EL järgmise finantsperspektiivi rahastamiskavades ja piiriülese koostöö programmides oleks kultuurivaldkonnal ja loomemajandusel senisest suurem osakaal.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 417, '3. Kaasamine, koostöö väärtustamine loome- ja erialaliitude ning valdkondlike keskustega:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (417, 418, 'riik peab eriala- ja loomeliite olulisteks partneriteks kultuuripoliitika kujundamisel, loome toimivad koostöövormid valdkondade esindajate kaasamiseks. Väärtustame valdkondlike keskuste panust Eesti kultuuri arendamisse;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (417, 419, 'jätkame kultuuripreemiate, sealhulgas elutöö preemiate maksmist vähemalt senises mahus.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 420, '4. Kultuuri kättesaadavuse parandamine:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (420, 421, 'edendame filmivaldkonna arengut, sealhulgas kodumaist filmilevikut ning Eesti filmitootmise tehnilist võimekust;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (420, 422, 'parandame professionaalse kunsti (kino, teater, kujutav kunst jms) kättesaadavust väikelinnades ja asulates;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (420, 423, 'loome Eesti Filmi Sihtasutuse juurde eraldi programmi laste ja noorte mängufilmide tegemiseks. Toetame Eesti animafilmi;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (420, 424, 'teeme veebis rohkem ja paremini kättesaadavaks kultuuriajakirju;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (420, 425, 'toetame veebipõhist raamatukogude infosüsteemi arendustegevusi ning tehnoloogilise võimaluste laiendamist nägemispuudega inimestele;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (420, 426, 'jätkame laulu- ja tantsupeoliikumise rahastamist pidudevahelisel ajal.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 427, '5. Eesti rahvale oluliste kultuuriehitiste püstitamine:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (427, 428, 'ehitame valmis Eesti Rahva Muuseumi ja Tallinna Muusikakeskkooli uued hooned;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (427, 429, 'jätkame ettevalmistusi Rahvusooper Estoniale uue maja ehitamiseks;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (427, 430, 'jätkame pühakodade, mõisakoolide ja looduslike pühapaikade programmi rahastamist, sealhulgas Euroopa Liidu vahenditest ning kohalike omavalitsuste kultuuri- ja spordiobjektide investeeringutoetusi;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (427, 431, 'tagame, et kõik riigi poolt algatatud suurprojektide (sealhulgas kommunismiohvrite memoriaali) arhitektuurikonkursid viiakse läbi koostöös valdkondlike loomeliitudega.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 432, '6. Kultuuriekspordi edendamine ja Eesti tutvustamine maailmas kultuuri varal:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (432, 433, 'loome kultuuriekspordi edendamiseks EASi ekspordi arendamise toetuste kõrvale kultuuriekspordi toetusmeetme, et valdkonnaspetsiifikat arvesse võttes toetada loomemajanduses tegutsejate võimalusi välisturgudel läbilöömiseks;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (432, 434, 'toetame valdkondlike arenduskeskuste tegevust, sh muusika, disaini, arhitektuuri ja filmi valdkonnas, eesmärgiga soodustada neis valdkondades ettevõtlust, kasvatada ekspordipotentsiaali, luua toimivad klastreid ja soodustada koostööd loomeettevõtete ja teiste majandusharude vahel;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (432, 435, 'kasutame kultuuriekspordi edendamisel aktiivselt Eesti diplomaatilist võimekust ning teeme koostööd teiste EL riikide välisteenistustega. Seame eesmärgiks muuta Eesti tunnustatud loomemajanduse- ja disainimaaks.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 436, '7. Kultuurihariduse suurem sidumine üldharidusega:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (436, 437, ' 1. jaanuarist 2014 kehtestame kuni 130 euro suuruse iga-aastase pearaha igale kooliealisele (6-19 aastasele) lapsele, mida saab kasutada huvialaringis osalemise eest tasumiseks;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (436, 438, 'jätkame koolide varustamist muusikariistadega;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (436, 439, 'tähtsustame muuseumit kui õpikeskkonda.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 440, '8. Eestimaa kultuurilise palge mitmekesistamine:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (440, 441, 'jätkame temaatiliselt eristuvate maakonnamuuseumide arendamist (sh muinsuskaitsealase tegevuse lisamist), et neist kujuneksid olulised maakondlikud külastus- ja teaduskeskused;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (440, 442, 'jätkame setu, Kihnu, saarte, Vana-Võru, Peipsiääre, Rannarootsi ja mulgi programme;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (440, 443, 'toetame rahvusvähemuste kultuurikollektiivide osavõttu suurematest Eesti kultuuriüritustest ja kultuurikollektiivide esinemist teistes Eesti regioonides;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (440, 444, 'aitame rahastada rahvusvähemuste kultuuri ja pühapäevakoole;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (440, 445, 'toetame hõimurahvaste koostööd.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 446, '9. Muinsuste ja kultuuripärandi tutvustamine, kaitsmine ning hoidmine:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (446, 447, 'vältimaks muinsuskaitse all olevate objektide pöördumatut hävimist, viime läbi nende inventeerimise ning vaatame üle kaitse alla võtmise kriteeriumid;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (446, 448, 'jätkame kultuuripärandi digitaliseerimist Eesti mäluasutustes. Muuhulgas muudame Eesti kultuuripärandi portaali ühtseks infoväravaks, mis võimaldab otsinguid erinevatesse süsteemidesse (muuseumid, raamatukogud, rahvusringhäälingu arhiivid, kultuuripärandi riiklik register);', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (446, 449, 'täiustame muuseumide infosüsteemi (MuIS), et lähitulevikus oleksid kõik riigimuuseumide kogud veebipõhised;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (446, 450, 'seome olemasolevad infosüsteemid Euroopa digitaalraamatukoguga Europeana, et meie väärtuslik kultuuripärand oleks kättesaadav kõikidele eurooplastele.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 451, '10. Autoriõiguste ja intellektuaalse omandi kaitsmine:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (451, 452, 'tagame intellektuaalse omandi igakülgse kaitse, viies seadusandluse vastavusse digiajastu nõuetega. Kaasajastame Autoriõiguse seaduse ning reguleerime puudulikult kaitstud valdkonnad;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (451, 453, 'võitleme tõhusalt piraatlusega ja koolitame õiguskaitseorganeid, et nad oskaksid uues digitaalses maailmas võidelda intellektuaalse omandiõiguse rikkumiste ja kuritarvitustega.', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (409, 454, '11. Loomemajanduse käsitlemine osana Eesti uuest majandusest:', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (454, 455, 'aitame kaasa kunstiinimeste ja ettevõtjate kokkuviimisele, majandus- ja kunstidistsipliine ühendavate õppekavade tekkele, loomelinnakute toetamisele ja kultuurimänedžeride koolitamisele;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (454, 456, 'toetame loomemajandust soosiva keskkonna väljaarendamist, muuhulgas investeeringuid loomelinnakutesse. Kaalume kultuuriministeeriumi loomemajanduse programmi suurendamist;', 21);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (454, 457, 'peame oluliseks ettevõtlusõppe võimaluste laiendamist kultuurieriala üliõpilastele.', 21);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (458, 'Valitsusliidu spordipoliitika eesmärk on elujõuline Eesti rahvas, tema õnnelik ja täisväärtuslik elu. Valitsusliit peab oluliseks liikumisharrastuse juurutamist juba imikueast ning lastesporti kui vahendit noorte eemalehoidmiseks alkoholi, tubaka ja narkootikumide tarbimisest. Liikumisharrastuste harjumuse tekitamine loob eeldused pikaks ja täisväärtuslikuks eluks väärikamas eas. Oluline on luua kõigile võimalused mitmekülgseks sportlikuks tegevuseks terve elu vältel, tagades tugeva ja laiahaardelise aluse meie tulevastele tippsportlastele ja olümpiavõitjatele.', 22);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (459, 'Selle eesmärgi saavutamiseks viib Valitsusliit ellu poliitikaid, mis lähtuvad järgmistest põhimõtetest:', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (459, 460, '1. Liikumisharrastuse edendamine:', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (460, 461, 'seame eesmärgiks, et aastaks 2014 on korrapärase liikumisharrastusega hõlmatud 35% asemel 45% elanikkonnast;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (460, 462, 'koostöös kohalike omavalitsustega aitame luua uusi kodulähedasi tervisespordiparke, kergliiklusteid, jooksu-, suusa-, ratta- ja matkaradasid ning soodustame harrastusspordi looduslike võimaluste väljaarendamist;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (460, 463, 'loome sportimisvõimalusi erivajadustega inimestele ja toetame nende sportlikku tegevust.', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (459, 464, '2. Noorte sportimisharjumuste toetamine:', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (464, 465, ' 1. jaanuarist 2014 kehtestame lapse kohta kuni 130 euro suuruse aastase huviringiraha, mida saab muuhulgas kasutada noortespordi finantseerimiseks, luues sellega lapsevanemale valikuvabaduse oma lapse sportliku arengu kavandamiseks;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (464, 466, 'pidades tähtsaks koolisporti, parandame sportimistingimusi koolides ning jätkame ujumise algõppe programmi toetamist;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (464, 467, 'väärtustame enam saavutusspordiga tegelevaid noori ning nende treenereid. Kaalume 50/50 süsteemi kasutuselevõtmist, mille puhul toetab riik poole summa ulatuses noortespordi III, IV ja V kutsekvalifikatsiooniga treenerite tasustamist;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (464, 468, 'laiendame noorsportlaste tervisekontrolli süsteemi.', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (459, 469, '3. Spordi rahastamise korrastamine:', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (469, 470, 'kujundame spordi rahastamise selgemad alused ja korrastame maksuregulatsioone sportimise soodustamiseks;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (469, 471, 'loome Euroopa Liidu järgmise finantsperspektiivi struktuurivahenditest programmi üleeestilise sporditaristu arendamiseks;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (469, 472, 'tunnustame tugevaid, hea juhtimisega spordiorganisatsioone.', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (459, 473, '4. Treener-pedagoogi elukutse väärtustamine:', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (473, 474, 'väärtustame treenerikutset ja kvalifikatsiooni ning pöörame enam tähelepanu treenerite täiendkoolitusele;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (473, 475, 'treenerkonnale järelkasvu ettevalmistamisel tuleb treenerite koolitamine muuta spordikesksemaks, suurendada praktikaõppe osakaalu ja oma ala spetsialistide kaasamist õppejõududena;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (473, 476, 'loome akadeemilises treeneriõppes õppijaile võimaluse taotleda Kultuurkapitali kehakultuuri ja spordi sihtkapitali stipendiumeid.', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (459, 477, '5. Tippspordi tugistruktuuride arendamine:', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (477, 478, 'toetame ratsionaalsemat ja elanikkonnas toimuvaid muudatusi arvestavat spordirajatiste kavandamist ning rajamist. Tippspordirajatiste planeerimisel ja ehitamisel võtame arvesse riiklikke prioriteete ning Eesti Spordiregistri andmebaasi. Arendame välja üleriigilised ja regionaalsed tippspordikeskused;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (477, 479, 'toetame tippsportlasi ning tagame neile konkurentsivõimelised treenimistingimused ja võimalused osalemiseks tiitlivõistlustel;', 22);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (477, 480, 'jätkame rahvusvaheliste tiitlivõistluste Eestis korraldamise toetamist.', 22);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (481, 'Valitsusliidu keelepoliitika eesmärk on tagada eesti keele kestmine ja areng kõigis eluvaldkondades läbi aegade. Eesti keel on eesti rahvusliku identsuse nurgakivi ning riigikeelena Eesti omariikluse olulisemaid tunnuseid.', 23);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (482, 'Tähtis pole üksnes hoida eesti keele tänast kasutusala, vaid seda sihikindlalt laiendada tulevikutehnoloogiatele ning uutele teadus- ja kultuurivaldkondadele. Veelgi järjekindlamaks peab muutuma keeleõppevõimaluste loomine muude emakeeltega Eesti elanikele. Tõhusalt tuleb toetada eesti keele õpet kodumaast eemal viibivaile eestlastele ja kõigile huvilistele. Valitsusliit on veendunud eesti keele suutlikkuses pidada sammu maailma muutumisega ja seab oma eesmärgiks selleks vajalike tegevuste sihipärase toetamise.', 23);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (483, 'Nende eesmärkide saavutamiseks peab Valitsusliit vältimatuks riigi ja kogu ühiskonna järjekindlat panustamist eesti keele arengusse, lähtudes järgmistest põhimõtetest:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 484, '1. Eesti oskuskeele järjekindel arendamine:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (484, 485, 'toetades Vabariigi Presidendi algatust eesti keele rikastamiseks, kujundame välja senisest süsteemsemad keelevärskenduse ja oskussõnavara loomise võimalused;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (484, 486, 'kanname hoolt, et õnnestunud sõna- ja mõisteloome tulemused leiaksid kiiresti koha kõrgkoolide õppematerjalides;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (484, 487, 'toetame erialaste uudisteoste tõlkimist ja avaldamist e-raamatuna.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 488, '2. Keele- ja aineõppe tugevam seostamine kõigil haridusastmeil:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (488, 489, 'tagame lisaks heale keeleõppele täpse ja selge keelekasutuse kujundamise kõigi õppeainete kaudu. Selleks uuendame õppekavasid ja õppematerjale ning panustame keeleõppe osakaalu suurendamisse õpetajakoolituses;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (488, 490, 'suurendame veebipõhiste õppematerjalide hulka ja kättesaadavust, et võimaldada igale soovijale osasaamine parimast keele- ja aineõppest.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 491, '3. Keele- ja kõnetehnoloogiate pidev edasiarendamine sihtrahastamise toel:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (491, 492, 'toetame kõnesünteesi tehnoloogia täiustamist, kõnetuvastuse valdkondlike rakenduste loomist ja kasutuselevõttu, eestikeelse ja eesti keelele suunatud tarkvara loomist ja rakendamist.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 493, '4. Keelekasutuse heade tavade juurutamine:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (493, 494, 'toetame keelekorraldusasutusi ja -vabakondi nende tegevuses, mis väärtustavad täpset, asjatundlikku ja stilistiliselt rikkalikku keelekasutust ühiskonnas;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (493, 495, 'laiendame keelenõustamise tehnilisi võimalusi, tagame sõnastike vaba kättesaadavuse veebis;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (493, 496, 'saavutamaks eeskujuliku kirjakeele kasutamine ametlikus suhtluses, õigusaktides ja avalikus teabelevis, panustame selleks vajalike koolituste korraldamisse.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 497, '5. Eesti keele õppe süvendatud jätkamine muu emakeelega inimestele:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (497, 498, 'eesti keelest erineva õppekeelega õppeasutustes tagame kõigil haridusastmeil kõrgel tasemel õppematerjalid ning laiendame keelekümblusvõimalusi;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (497, 499, 'panustame õpetajate ettevalmistusse ja täiendkoolitusse;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (497, 500, 'muukeelsetes kutseõppeasutustes ja -rühmades peame eriti silmas õppijate erialase keeleoskuse arendamist;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (497, 501, 'toetame ülikoolide valmisolekut pakkuda esimestel õppesemestritel eesti keele tihendatud õpet ebapiisava keeleoskusega üliõpilastele;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (497, 502, 'suurendame riigikeeleõppe võimalusi täiskasvanuile;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (497, 503, 'seame sihiks nõustamisteenuse loomise muukeelsete koolide eesti keelele ülemineku toetamiseks ja keelehaldusküsimuste lahendamiseks.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 504, '6. Eesti keele murrete väärtustamine:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (504, 505, 'näeme eesti keele murretes tagatist ja ammendamatut allikat eesti ühiskeele kestvale arengule. Toetame sihtprogrammide kaudu kõigi tugevamate piirkondlike keele- ja kultuuritavade õpet, edasiarendamist, praktiseerimist ja laialdast tutvustamist.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 506, '7. Eesti keele toetamine välismaal:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (506, 507, 'peame vajalikuks tagada välismaal elavate ja töötavate eestlaste kontaktid kodumaaga ja nende lastele võimalused õppida eesti keelt;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (506, 508, 'seame sihiks, et üha enamates välisülikoolides avataks eesti keele ja kultuuri õppetoolid.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 509, '8. Keeleseaduse täitmise järelevalve: tagame, et igaühel on kõikjal Eestis tõrgeteta võimalus ametlikus suhtluses ja teeninduses ajada asju eesti keeles.', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (483, 510, '9. Ajaloo teadvustamine:', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (510, 511, 'edendame isamaalist kasvatust ja toetame isamaaliste noorteorganisatsioonide tegevust;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (510, 512, 'jätkame represseeritute toetamist vastavalt okupatsioonirežiimide poolt represseeritute seadusele;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (510, 513, 'toetame Eesti riikliku iseseisvuse eest võidelnute tunnustamist Riigikogu otsusega;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (510, 514, 'toetame president Meri algatust rahvusvahelise kommunismikuritegude muuseumi asutamiseks, samuti Tallinna kommunismiohvrite memoriaali rajamist ning jätkame Kaitseväe kalmistu ja Vabadussõja mälestusehise taastamist;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (510, 515, 'toetame Rahvusvahelise Kommunismikuritegude Uurimise Komisjoni moodustamise algatust sarnaselt Rahvusvahelisele Kriminaalkohtule;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (510, 516, 'edendame Eesti lähiajaloo uurimist ja selgitamist rahvusvahelisele üldsusele;', 23);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (510, 517, 'toetame lähiajaloo tutvustamist koolides erinevates regioonides ja keeltes.', 23);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (518, 'Valitsusliidu eesmärk on tagada võrdsed võimalused edukaks toimetulekuks ja heaoluks kõigile Eestis elavatele inimestele, olenemata rahvuslikust kuuluvusest ja emakeelest. Valitsusliit tagab, et Eesti Vabariigis on kaitstud iga siin elava isiku põhiõigused ja -vabadused.', 24);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (519, 'Lisaks eestlastele kui põhirahvusele elab siin hulgaliselt muu rahvuslik-kultuurilise taustaga ja erasuhtluses teisi keeli kasutavaid inimesi. Valitsusliit näeb mitmekesisuses rikastavat võimalust Eesti eduks ja arenguks.', 24);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (520, 'Eesmärgi saavutamiseks peab Valitsusliit vajalikuks, et riiklik lõimumiskava sisaldaks alljärgnevaid struktuurseid reforme ja põhimõtteid:', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (520, 521, '1. Vähemusrahvusest eestimaalaste, eriti noorte, konkurentsivõime tugevdamine tööturul:', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 522, 'edukas toimetulek Eestis eeldab eesti keele oskust. Viime lõpule ülemineku eestikeelsele gümnaasiumile;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 523, 'panustame veelgi enam eesti keele õppe ja eestikeelse aineõppe kättesaadavusse ja kvaliteeti kõigil haridustasandeil – venekeelses lasteaias ja koolis peab eesti keele õpe toimuma iga päev. Muudame vastavate õpetajate, ainekavade ja õppematerjalide ettevalmistamise süsteemseks ja järjekindlaks;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 524, 'tugevdame projektipõhiseid võimalusi õpetajate täiendõppeks ja muid tugitegevusi;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 525, 'toetame keelekümblusmetoodika laiemat rakendamist alates lasteaiast;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 526, 'majanduslike võimaluste tekkel taassuurendame laste keeleõppelaagrite (perelaagrite) ja ekskursioonide korraldamise võimalusi;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 527, 'peame oluliseks, et kõrgharidust omandada soovivad mitte-eestlased õpiksid edasi Eesti ülikoolides;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 528, 'suurendame mitte-eestlastest avalike teenistujate ning haridus- ja kultuuritöötajate võimalusi erialase keelevaldamise kinnistamiseks;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 529, 'arendame edasi tööjõuvahetuse programme. Tihendame koostööd Töötukassa, Sihtasutuse Meie Inimesed (MISA) ja kohalike omavalitsuste vahel, pakkumaks vähemuspäritoluga töötutele tõhusamat tuge ja õpet;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 530, 'tagame riigikeeleõppe kättesaadavuse ka mitte-eestlastest töötutele, pensionäridele ja puuetega inimestele;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 531, 'arendame järelevalvet täiskasvanuile eesti keele õpetamise, sh koolitajate kvaliteedi üle;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (521, 532, 'algatame riikliku lõimumiskava pideva jälgimise, eesmärgiga kohandada selle rakenduskavu muutuvatele vajadustele.', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (520, 533, '2. Eri ühiskonnarühmade omavahelise suhtluse tihendamine:', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (533, 534, 'väärtustame ja toetame algatusi, mis viivad ühiste eesmärkide saavutamiseks kokku eri rahvusrühmadest pärinejaid. Majanduslike võimaluste tekkel tugevdame Sihtasutuse Meie Inimesed ja Kodanikuühiskonna Sihtkapitali võimalusi sellesuunaliste projektide toetamiseks;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (533, 535, 'jätkame mitmekeelset teabepakkumist, sealhulgas rahvusringhäälingus ja sotsiaalmeedias.', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (520, 536, '3. Eesti põlisvähemuste kultuuripärandi väärtustamine ja avalikkusele kättesaadavaks tegemine:', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (536, 537, 'hindame kõrgelt Eesti ajalooliste põlisvähemuste (juudid, baltisakslased, vene vanausulised, eestirootslased, ingerisoomlased) pikaajalist panust Eesti rikkaliku kultuuripildi väljakujunemisse. Toetame selle kultuuripärandi hoidmist, uurimist ja väärikat eksponeerimist, sealhulgas ka välismaal, vastavate kultuuriühenduste, riigi, omavalitsuste ja erasektori koostööl;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (536, 538, 'eelarvevõimaluste avanemisel parandame Peipsi-ääre arengukava, pühakodade programmi, vana-slaavi keeleõppe ja kultuuripärandi uurimise rahastamist;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (536, 539, 'korrastame Kloogal asuva holokausti memoriaali.', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (520, 540, '4. Vähemuskultuuride püsimise toetamine:', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (540, 541, 'austame iga inimese õigust oma rahvuskultuurilisele identiteedile;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (540, 542, 'peame vältimatuks rahvusvähemuste kultuuriühenduste ja pühapäevakoolide jätkuvat toetamist riigi ja omavalitsuste poolt;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (540, 543, 'eelarvevõimaluste tekkel suurendame soome-ugri rahvaste toetusprogrammi kõrghariduse omandamiseks Eestis.', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (520, 544, '5. Jätkuvalt austades iga inimese vaba tahet oma kodakondsuse määratlemisel, parandame Eestis elavate teiste riikide kodanike ja määratlemata kodakondsusega isikute teavitamist Eesti kodakondsuse omamise eelistest ja võimalustest:', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (544, 545, 'tagame mittekodanikest vanemaile informatsiooni võimaluste kohta omandada Eesti kodakondsus oma kuni 15-aastasele lapsele;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (544, 546, 'pöörame erilist tähelepanu vast-naturaliseerunud Eesti Vabariigi kodanikele, et hõlbustada nende sulandumist ühiskonda;', 24);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (544, 547, 'kodakondsuspoliitika põhimõtteid ei muudeta.', 24);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (548, 'Valitsusliidu keskkonnapoliitika eesmärk on vastutustundliku suhtumise kujundamine loodusesse ning Eesti inimestele rahva püsimist toetava puhta ja looduslikult mitmekesise elukeskkonna säilimine põlvest põlve. Valitsusliit paneb erilist rõhku Eesti inimeste loodussäästliku mõtteviisi kujundamisele ning maa- ja loodusvarade heaperemehelikule kasutamisele.', 25);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (549, 'Nende eesmärkide saavutamiseks lähtub Valitsusliit oma keskkonnapoliitikas järgmistest põhimõtetest:', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (549, 550, '1. Puhta elukeskkonna kindlustamine igale Eesti elanikule:', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (550, 551, 'jätkame reoveepuhastite uuendamist, kõik ühisveevärki tarbivad Eesti elanikud saavad puhta joogivee hiljemalt aastaks 2013;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (550, 552, 'seame eesmärgiks puhtad metsaalused, üldise jäätmetekke vähendamise ning jäätmete taaskasutamise suurendamise, muuhulgas energiatootmiseks;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (550, 553, 'arendame jäätmete taaskasutussüsteeme, et need oleksid tarbijale võimalikult mugavad. Toetame kohalikke omavalitsusi jäätmekogumispunktide rajamisel;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (550, 554, 'õhusaaste ja mürakoormuse vähendamiseks linnades kindlustame küllaldase haljastuse;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (550, 555, 'õhusaaste leevendamiseks siirdume järk-järgult väiksema süsinikukoormusega majandusmudelile. Energia ja soojuse tootmisel eelistame väiksema keskkonnamõjuga taastuvaid (kodumaiseid) energiaallikaid. Jätkame energiasäästuprojektide toetamist;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (550, 556, 'soovime avada eraldi meetme nõukogude aegse maastikureostuse (tondilosside) likvideerimiseks;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (550, 557, 'võtame fookusesse ka võitluse kaaskodanike avaliku korra rikkumisest lähtuva mürareostusega.', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (549, 558, '2. Täisväärtuslikuks eluks vajaliku loodusliku mitmekesisuse säilitamine:', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (558, 559, 'peame looduskaitsemeetmete rakendamisel oluliseks ökosüsteemide sidusust ning keskkonnakasutuse eesmärgipärast ja mõistlikku piiramist;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (558, 560, 'kaitsealade puhul peame tähtsaks kaitsekavade tegelikku rakendamist, mitte nende formaalset laiendamist uutele aladele;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (558, 561, 'tagame Nabala karstiala ja Tuhala Nõiakaevu säilimise;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (558, 562, 'jätkame eraomanike toetamist, kes oma maal loodust kaitsevad ja aitavad traditsiooniliste majandamisviisidega kaasa pärandmaastike ja -koosluste säilimisele;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (558, 563, 'tagame traditsiooniliste kalajõgede elujõulisuse, hea seisundi ja kalade läbipääsu paisudest.', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (549, 564, '3. Eesti maavarade kui rahvusliku rikkuse heaperemehelik majandamine:', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (564, 565, 'riik võtab Eesti maavaravarude kasutuselevõtu ettevalmistamisel ja korraldamisel juhtiva rolli;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (564, 566, 'kindlustame, et ressursi- ja keskkonnakasutuse tasude määrad on pikaajaliselt ette teada;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (564, 567, 'pöörame lisaks põlevkivivarudele ja ehitusmaavaradele tähelepanu ka seni kasutuseta maavarade uurimisele ning toetama uute tehnoloogiate kasutuselevõttu;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (564, 568, 'uute kaevanduste avamine toimub kokkuleppel kohaliku kogukonnaga.', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (549, 569, '4. Loodusvarade keskkonnasäästlik kasutamine:', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (569, 570, 'laiendame aheraine kasutust ehitusmaterjalina teede ja parklate rajamisel;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (569, 571, 'ammutame lõpuni pooleldi kasutatud turbaalad ning toetame kasutatud turbaalade rekultiveerimist;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (569, 572, 'loome tingimused kestlikuks, kasumlikuks ja isemajandavaks erametsanduseks. Selleks viime läbi tulumaksumuudatuse, mille kohaselt lubame raieõiguse või uuendusraie käigus raiutud metsamaterjali võõrandamisel saadud maksustatavast tulust maha arvata kuni kolme aasta jooksul pärast võõrandamist tehtud metsa majandamise kulud;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (569, 573, 'toetame erainitsiatiivi ühistulise tugisüsteemi arendamisel, mis oleks metsaomanikule toeks metsatööde korraldamisel ja puidu turustamisel;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (569, 574, 'põline riigimets jääb riigi omandisse;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (569, 575, 'laiendame harrastuspüügivõimalusi kudemiskohtade ja kalapüügikohade taastamise kaudu;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (569, 576, 'kaasajastame jahinduse õiguslikku korraldust.', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (549, 577, '5. Puhta ja turvalise Läänemere eest võitlemine:', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (577, 578, 'seame eesmärgiks saavutada Läänemerele aastaks 2020 Euroopa Liidu kriteeriumidele vastav hea seisund. Selleks rakendame tegevuskava, mis arvestab siseveekogusid ja merd tervikliku ökosüsteemina, hajutab tihedast tankeriliiklusest tulenevaid keskkonnariske ning suurendab reostustõrjevõimekust;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (577, 579, 'pöörame tähelepanu rannikumere puhtana hoidmisele;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (577, 580, 'osaleme aktiivselt rahvusvahelises Läänemere kaitse ja kasutamise alases koostöös ning võitleme Läänemere-äärsete riikide ühtse keskkonnaseiresüsteemi loomise eest;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (577, 581, 'viime läbi uuringud merekaitsealade kiiremaks määratlemiseks ja Läänemere seisundi täpseks hindamiseks.', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (549, 582, '6. Keskkonnateadlikkuse kasvatamine:', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (582, 583, 'toetame keskkonnahariduse edendamist. Selleks tagame keskkonnahariduse andmiseks pädevate õpetajate juurdekasvu ja toetame loodusõppeprogrammide arendamist;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (582, 584, 'riik näitab eeskuju, rajades uued avaliku sektori hooned maksimaalselt energiasäästlikena ning rakendades kõrgemaid keskkonnastandardeid transpordivahendite hangetel;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (582, 585, 'säilitame juurdepääsu merele ja veekogudele ning riigimetsa koos metsa traditsiooniliste andide (puhas õhk, seened, marjad) kasutamise võimalusega;', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (582, 586, 'keskkonnateadlikkuse suurendamiseks tagame kõigile e-juurdepääsu riigi valduses olevatele keskkonnaandmetele.', 25);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (549, 587, '7. Arukam haldus: ehitus- ja planeerimistegevuse seadustiku loomise käigus kaalume riigiarhitekti ametikoha loomist ning planeerimistegevuse viimist keskkonna- või majandus- ja kommunikatsiooniministeeriumisse.', 25);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (588, 'Valitsusliidu maaelu-, põllumajandus- ja regionaalpoliitika eesmärk on maapiirkondade kõrge elukvaliteet ning hästi tasustatud töökohtade juurdekasv maal. Selleks teeb Valitsusliit panuse taristu arendamisele, ettevõtluskeskkonna jätkuvale parandamisele ning inimeste koolitamisele ja nõustamisele. Valitsusliit peab seejuures oluliseks koostööd omavalitsuste, kodukandi- ja külaliikumisega, seltside, koguduste ning ettevõtjatega.', 26);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (589, 'Eesmärgi saavutamiseks viib Valitsusliit läbi poliitikaid, mis lähtuvad järgmistest põhimõtetest:', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (589, 590, '1. Eesti põllumehele EL tootjatega võrdsete konkurentsitingimuste tagamine:', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (590, 591, 'seisame selle eest, et Eesti põllumehele rakenduksid alates järgmisest EL eelarveperioodist (2014) kõigi teiste Euroopa Liidu tootjatega võrdsed konkurentsitingimused;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (590, 592, 'toetame Ühise põllumajanduspoliitika (ÜPP) muutmist üha rohkem maaelu kui terviku (maaelu arengukava alusel eraldatavateks) toetusteks. Selleks tuleb ÜPP lahti siduda mineviku tootmismahtudest ning maksta otsetoetusi järjest rohkem avaliku huvi alusel, nagu omamaise toidu kättesaadavus, piisav sissetulek tootmise kestlikuks käigushoidmiseks, põllumajandusmaastike hooldus, bioloogiline mitmekesisus, maapiirkondade elujõulisus;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (590, 593, 'seisame selle eest, et Ühise põllumajanduspoliitikat rahastataks jätkuvalt Euroopa Liidu ühisest eelarvest;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (590, 594, 'tagame Ühise põllumajanduspoliitika ja Eesti Maaelu arengukava riigi poolse kaasfinantseerimise;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (590, 595, 'tagame põllumajanduse otsetoetuste siseriikliku juurdemakse (top-up) 2012. aasta eest Euroopa Liidu lubatud ulatuses;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (590, 596, 'jätkame siseriiklike põllumajandustoetuste maksmist senises mahus.', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (589, 597, '2. Maaelu mitmekesistamine, ettevõtluse paljususe edendamine Ühtse põllumajanduspoliitika raames:', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (597, 598, 'toetame uute innovaatiliste ettevõtlusvaldkondade levikut maal lisaks põllumajandusele väike- ja pereettevõtluse mitmekesiste tegevusalade arengut. Senisest enam võimalusi näeme metsamajanduse ja vesiviljelusega seotud valdkondades;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (597, 599, 'uuendusliku maaettevõtluse arendamiseks tõstame teavitustegevuse ja nõuandeteenuse taset, parandades selleks nõustajate kvalifikatsiooni.', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (589, 600, '3. Ühistegevuse ja koostöö edendamine:', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (600, 601, 'toetame oma kodukanti arendavaid, loovaid ja tegusaid liidriomadustega inimesi ning peame oluliseks selgitada ühistegevuse eeliseid;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (600, 602, 'toetame senisest enam majanduslikku ühistegevust nii tootmise, töötlemise kui turustamise valdkonnas;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (600, 603, 'toetame jätkuvalt külaliikumise ja talu kui traditsioonilise tootmisüksuse arendamist ning külavanemate institutsiooni tugevdamist vallavolikogu ja vallavalitsuse koostööpartnerina. Soosime omavalitsuste, kodukandi-, külaliikumise ja kohaliku omaalgatuse (Leader) programmi osaliste, mittetulundusühingute, koguduste ja ettevõtjate koostööd.', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (589, 604, '4. Eeskujuliku taristu arendamine maal:', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 605, 'ehitame uue põlvkonna fiiberoptilistel kaablitel põhineva ülikiire lairibaühenduse kõikide Eesti maapiirkondadeni;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 606, 'tagame elektrienergia varustuskindluse, ehitame ringtoite kaabli Hiiumaa ja mandri vahele;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 607, 'jätkame riigi kruusateedele tolmuvaba katte ehitamist, kasutades Põhjamaade tehnoloogiaid;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 608, 'jätkame Tallinna ja Tartu ringtee, Tallinn-Tartu maantee neljarealiseks ehitamist, Saaremaa püsiühenduse ja Via Baltica rajamist ning Tallinn-Narva maantee rekonstrueerimist;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 609, 'jätkame reoveepuhastite uuendamist, kõik ühisveevärki tarbivad Eesti elanikud saavad puhta joogivee hiljemalt aastaks 2013;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 610, 'tervishoiuteenuse tagamiseks kõikjal Eestis loome asendusperearstide süsteemi, mis tagaks perearsti olemasolu igale elanikule ka siis, kui oma perearst puhkab või tekib muu ettenägematu takistus teenuse osutamiseks raskesti ligipääsetavates paikades;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 611, 'erakorralise meditsiini ja päästeteenuse osutamiseks kindlustame kõikide kõrgema tähtsusega väljakutsete puhul helikopteriteenuse olemasolu saartel;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 612, 'siseturismi elavdamiseks ja uute töökohtade loomiseks investeerime EL vahendeid väikesadamate arendamiseks;', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (604, 613, 'toetame ajaloo- ja kultuuripärandi taastamist ja selle toomist kohalikku ja turismitarbelisse kasutusse. Lisame Eesti turismi arengukavasse meie omanäolise toidukultuuri arendamise ja propageerimise. Jätkame Eesti Toidu programmi.', 26);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (589, 614, '5. Halduse korrastamine: koondame kalandusvaldkonna korralduse, välja arvatud ressurside üle teostatava järelevalve põllumajandusministeeriumisse.', 26);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (615, 'Valitsusliidu välispoliitika eesmärk on Eesti julgeoleku ja heaolu kasvu kindlustamine. ', 27);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (616, 'Eesti julgeoleku ja heaolu kasvu kindlustamiseks peab Valitsusliit oluliseks lähtuda Eesti välispoliitikas järgmistest põhimõtetest:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 617, '1. Eesti on aktiivne Euroopa Liidu, NATO, ÜRO, Euroopa Julgeoleku- ja koostööorganisatsiooni (OSCE), Euroopa Nõukogu ja teiste rahvusvaheliste organisatsioonide poliitikate kujundamisel:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (617, 618, 'Eesti teeb ettevalmistusi eesistumisperioodiks Euroopa Liidus 2018. aastal;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (617, 619, 'peame oluliseks Eesti pääsu ÜRO Inimõiguste nõukokku 2012. aastast ning ÜRO Julgeolekunõukokku 2020. aastast.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 620, '2. NATO kui Eesti peamine julgeolekutagatis:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (620, 621, 'panustame NATO strateegilise kontseptsiooni elluviimisse, sealhulgas olulistesse NATO missioonidesse;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (620, 622, 'missioonide kõrval peame Eesti olulisimaks panuseks NATO küberkaitse arendamist, milles soovime olla üheks juhtriikidest;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (620, 623, 'peame terrorismivastast võitlust Afganistanis üheks NATO olulisimaks missiooniks turvalisema homse kujundamisel ning narkokaubanduse leviku tõkestamisel. Otsuse selle missiooni lõpetamiseks vajalike tingimuste täitmise kohta peavad NATO liikmed tegema ühiselt.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 624, '3. Aktiivne äri- ja kultuuridiplomaatia, Eesti ettevõtjate, ekspordi ja kultuuritegelaste toetamine Välisministeeriumi ja Ettevõtluse Arendamise Sihtasutuse välisesinduste koostöös:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (624, 625, 'sõlmime võimalikult paljude riikidega topeltmaksustamise vältimise ning investeeringute kaitse lepingud;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (624, 626, 'laiendame aukonsulite võrgustikku ning kasutame Eesti huvides rohkem koostööd teiste EL riikide diplomaatiliste esindustega.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 627, '4. Toetus Euroopa Liidu ja NATO senise laienemispoliitika jätkamisele:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (627, 628, 'toetame Euroopa Liidu liitumiskõneluste jätkamist Lääne-Balkani riikidega, Islandi ja Türgiga ning valmisolekut kõneluste alustamiseks organisatsiooni mittekuuluvate Ida-Euroopa riikidega;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (627, 629, 'toetame NATO avatud uste poliitika jätkamist nii Balkanil, Lõuna-Kaukaasias kui ka mujal Euroopas.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 630, '5. Turvalisuse suurendamine Euroopa Liidu piiridel:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (630, 631, 'peame tähtsaks EL naabruspoliitikat ja idapartnerlust, arendame Idapartnerluse Koolituskeskust;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (630, 632, 'peame oluliseks Euroopa uute demokraatiate toetamist ning rahvusvahelise õiguse põhimõtete kaitsmist Euroopa Liidu vahetus naabruses, mistõttu ei ole alternatiivi Gruusia territoriaalsele terviklikkusele. Ka tuleb tagada Moldova territoriaalne terviklikkus ning leida lahendus Transnistria külmutatud konfliktile.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 633, '6. Tihedad suhted kõigi oma naabritega:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (633, 634, 'peame tähtsaks Läänemere strateegia rakendamist Läänemere piirkonna arenguks, muuhulgas uute transpordi- ja energiaühenduste loomisel;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (633, 635, 'tähtsustame ja peame arendamist väärivaks NB 8 ehk Põhjamaade ja Balti riikide igakülgset koostööd;', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (633, 636, 'lisaks tihedatele ja headele suhetele Euroopa Liitu ja/või NATOsse kuuluvate naabritega, on Eesti huvides ka heanaaberlikud suhted Venemaaga. Toetame konkreetseid ja praktilisi algatusi vastastikuste suhete arendamiseks.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 637, '7. Inimõiguste ja heade valitsemistavade rahvusvaheline edendamine:', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (637, 638, 'Eesti näeb arengukoostöösse ja humanitaarabisse panustamises olulist välis- ja julgeolekupoliitika vahendit ning annab oma võimetele vastava panuse maailma vaesuse vähendamisse ning demokraatia, õigusriigi ja heade valitsemistavade põhimõtete kinnistamisse arengukoostöö sihtriikides; tegutseme aktiivselt totalitarismi kuritegude teadvustamisel ja nende ühemõttelise hukkamõistu saavutamisel.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 639, '8. Toetame igati transatlantilise koostöö arendamist kõigis valdkondades ning Eesti-USA suhete süvendamist.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 640, '9. Taotleme Eesti kodanikele viisavaba pääsu enamatesse riikidesse.', 27);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (616, 641, '10. Eesti huvide paremaks esindamiseks tugevdame Eesti välisesinduste võrgustikku keskendudes peamistele julgeolekualastele ja majanduslikele prioriteetidele ning Eesti kodanike huvide kaitsele.', 27);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (642, 'Valitsusliidu julgeoleku- ja riigikaitsepoliitika eesmärk on parimate julgeolekutagatistega iseseisev Eesti riik, mis on kaitseks sisemisele ja välisele rahule, praeguste ja tulevaste põlvede eneseteostusele ning eesti rahvuse, keele ja kultuuri säilimisele läbi aegade. Eesti riigi kaitsmine on meie ühisväärtus, -hüve ning -vastutus. Eesti julgeolekut tugevdab sidus kodanikuühiskond, kus teadlikul kodanikuaktiivsusel on oluline roll julgeoleku ja turvatunde edendamisel. Riigikaitse oluline eeldus on kodanike kaitsetahe.', 28);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (643, 'Nende eesmärkide saavutamiseks arendab Valitsusliit Eesti rahvusliku julgeoleku võimekust ja riigikaitset järgmiste põhimõtete kohaselt:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 644, '1. Eesti peamine julgeolekutagatis on kollektiivkaitse ja NATO:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (644, 645, 'Eesti riigikaitse tugineb esmasele iseseisvale kaitsevõimele ning NATO kollektiivkaitsele. Eesti sõjaline kaitse on kavandatud kollektiivkaitseoperatsioonina;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (644, 646, 'kollektiivkaitse veenvuse tugevdamiseks tuleb suurendada NATO kohalolekut, arendada kahepoolset kaitsekoostööd liitlastega ning tagada Kaitseväe koostegutsemisvõime liitlasvägedega;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (644, 647, 'Eesti esmane iseseisev kaitsevõime peab tagama eeldused kollektiivkaitse toimivaks rakendumiseks.', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 648, '2. Eesti riigikaitse kiire rakendatavus ja selle eelduseks oleva juhtimisselguse tagamine:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (648, 649, 'riigikaitse arendamisel lähtutakse riigikaitse laiast käsitlusest vastavuses kehtestatud Riigikaitsestrateegiaga. Kindlustame, et kõik riigiasutused valmistuvad oma riigikaitseliste ülesannete täitmiseks võimalike ohtude korral ning tagame, et sõjaline kaitse ei arene ülejäänud ühiskonnast eraldiseisvalt;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (648, 650, 'toetame riigikaitseliste Põhiseaduse muudatuste vastuvõtmist Riigikogu poolt;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (648, 651, 'algatame rahu- ja sõjaaja riigikaitseseaduste kaasajastamise. Toimekindluse tagamiseks muudame rahu- ja sõjaaja riigikaitselised juhtimissüsteemid ning vastutusalad võimalikult sarnasteks;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (648, 652, 'riigikaitse juhtimise korraldamisel lähtutakse kaitsejõududest kui täitevvõimu osast. Tagame erinevate riigikaitseliste tegevuste juhtimise koordineerituse ning juhtimise selguse ja läbipaistva tsiviilkontrolli;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (648, 653, 'jätkame riigi lennuvahendite ja laevade ning nende käitamiseks vajaliku teenindusressursi koondamist. Loome aluse militaar- ja päästeressursi võimete koostoimeks;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 654, '3. Aktiivne panustamine NATO ja Euroopa Liidu ühispoliitikatesse:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (654, 655, 'osaleme aktiivselt NATO arengute suunamisel, peame õigeks NATO nähtavuse ja kohaloleku suurendamist;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (654, 656, 'taotleme NATO Balti õhuturbemissiooni muutmist alaliseks;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (654, 657, 'tagame Eesti osaluse operatsioonidel senises mahus kooskõlas NATO sihttasemega (10% maaväe koosseisust);', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (654, 658, 'osaleme võimetekohaselt Euroopa Liidu ühise julgeoleku- ja kaitsepoliitika alastes ettevõtmistes, sealhulgas EL operatsioonidel;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (654, 659, 'koos sõjaliste missioonide planeerimise ja läbiviimisega arendame ka tsiviilmissioonidesse panustamise võimekust;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (654, 660, 'süvendame kaitsekoostööd USA-ga, sealhulgas operatsioonidel;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (654, 661, 'panustame aktiivselt Põhjala-Balti riikide kaitsealasesse koostöösse eesmärgiga muuta see peamiseks regionaalseks kaitsekoostöö formaadiks Põhja-Euroopas.', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 662, '4. Riigikaitse toetumine kodanike vabatahtlikule kaasatusele ja kaitsetahtele:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (662, 663, 'jätkame ja laiendame vabatahtlikkuse alusel toimuvat riigikaitseõpetust kesktaseme õppeasutustes eesmärgiga suurendada vastavate koolide arvu 150-ni;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (662, 664, 'peame oluliseks toetada Kaitseliidu arendamist ning kasvavat rolli riigikaitses. Tagame Kaitseliidu ja tema eriorganisatsioonide stabiilse arengu, Kaitseliidu rahastamiseks näeme ette kindla osa sõjalise riigikaitse eelarvest. Võtame vastu uue Kaitseliidu seaduse.', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 665, '5. Eesti kaitsekulude taseme tõstmine 2% SKTst. Eesti iseseisvus ja julgeolek on väärtused, mille arvelt ei tohi kokku hoida:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (665, 666, 'saavutame aastaks 2012 kaitsekulude tasemeks 2% SKTst ning tagame selle taseme stabiilsuse;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (665, 667, 'alustame Jägala linnaku väljaehitust, kavandades selle valmimise 4 aastaga;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (665, 668, 'tagame Ämari lennubaasi täieliku tegutsemisvalmiduse saavutamise aastaks 2015;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (665, 669, 'jätkame Kaitseväele moodsa relvastuse hankeid. Parimate hanketingimuste saamiseks teeme vajadusel koostööd partnerriikidega;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (665, 670, 'kindlustame Kaitseväele tänapäevased elamis- ja väljaõppetingimused.', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 671, '6. Riigi sõjalise kaitse professionaalsuse edendamine:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (671, 672, 'riigikaitse mehitamisel lähtume senisest segasüsteemist, mis hõlmab ajateenistust üldise kaitsetahte ja oskuste arendajana, elukutselist kaadrikaitseväge, reservväelasi ning Kaitseliitu ühinenud vabatahtlikke. Võtame vastu uue Kaitseväeteenistuse seaduse;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (671, 673, 'tugineme ajateenistuse arendamisel Riigikaitse 10-aastases arengukavas sätestatule. Kaalume üleminekut 6-9-12-kuulisele ajateenistuse süsteemile järgmise 10-aastase arenguplaani koostamise käigus aastal 2012. Eelistatult tuleb ajateenistusse võtta kõik omal initsiatiivil sinna astuda soovijad;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (671, 674, 'kasvatame kaadrikaitseväelaste arvu 125 võrra igal aastal kuni piirarvu 4000 saavutamiseni.', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 675, '7. Rahvusvahelise initsiatiivi hoidmine küberjulgeoleku arengutes:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (675, 676, 'a. arendame aktiivselt Eesti küberkaitsevõimekust, sealhulgas siseriiklikus ja rahvusvahelises koostöös. Tugevdame koostööd NATO liitlastega tugevdatakse Küberkaitsekeskust eesmärgiga muuta see juhtivaks rahvusvaheliseks kompetentsikeskuseks;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (675, 677, 'b. toetame Kaitseliidu juurde loodud küberkaitseliitu. Suurendame küberkaitse alast väljaõpet Kaitseväes.', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 678, '8. Eesti kaitsetööstuse ja kaitseotstarbelise teadus- ning arendustegevuse toetamine:', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (678, 679, 'koostöös Kaitsetööstuse Liiduga jätkame rahvusliku kaitsetööstuse arendamist eesmärgiga saavutada arvestatav ekspordivõimekus mõnes tegevussuunas;', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (678, 680, 'kasvatame kaitsealase teadus- ja arendustegevuse rahastamist lähtudes eelkõige Kaitseväe vajadustest ja suunatuna kaitsetööstuse arendamisele.', 28);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (643, 681, '9. Algatame Eesti iseseisvuse ja julgeoleku nimel välismissioonidel osalenute kodanikuühiskonda ja -ellu tõhusa taasrakendamise poliitika: toetame veteranide organisatsiooni, töötame välja tervikliku veteranidepoliitika missioonidel osalenud kaitseväelaste toetuseks ning alustame vastavate teenuste arendamist ja võimekuste suurendamist.', 28);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (682, 'Valitsusliidu eesmärk on riigikorraldus, mis toetab Eesti kodanike loomulikku soovi elada vaba ja täisväärtuslikku elu. Me soovime, et Eesti riik kasutaks igat maksumaksja poolt makstud senti säästlikult, eesmärgiga pakkuda selle eest parimat võimalikku avalikku taristut ja teenust võimalikult paljudele. Me soovime, et Eesti oleks tugeva kogukondliku võrgustiku ja identiteediga, loogiliselt, lihtsalt ja mõistuspäraselt korraldatud ning tõhusalt toimiv demokraatlik riik.', 29);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (683, 'Nende sihtide saavutamiseks peab Eesti riigi- ja kohalik haldus järgnevatel kümnenditel arvestama jätkuva linnastumise ja elanikkonna vananemisega. Kohaliku halduse kaasajastamine peab toimuma järk-järgult, paindlikke lahendusi võimaldades ning kogukondlikke võrgustikke ja identiteete lõhkumata. Valitsusliit on veendunud, et tugev kogukondlik identiteet on parim demokraatia tagatis.', 29);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 684, '1. Toetame Eesti Põhiseaduse ja põhiseaduslike seaduste järjepidevat akadeemilist analüüsi, et vältida Põhiseaduse põhjendamatut muutmist, kuid hoida meie riigi alusseadus ja selles sisalduvate väärtuste kaitse ajakohasena.', 33);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 685, '2. Loome õigusliku raamistiku demokraatia arendamise sihtasutuste tekkeks erakondade kõrvale, mille ülesandeks on Euroopa Liidu naabruspoliitika, maailmavaatelise kodanikuhariduse ja ühiskondliku poliitilise mõtte edendamine.', 33);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (686, 'Valitsusliit peab riigi arengus oluliseks eelkõige kodanikuühiskonna tugevnemist. Eesti riik peab tugevnema mitte uute ametiasutuste loomise ja täiendavate ametnike teenistusse võtmise teel, vaid kaasates kodanikuühendusi riigivalitsemisse ja võttes laiemalt kasutusele uusi tehnoloogiaid. Valitsusliit soovib, et vabad kodanikud osaleksid oma ühenduste kaudu tegusalt nii ühiskondlike otsuste langetamise protsessis kui ka ühiskonnaelu korraldamises. Selleks peame oluliseks ellu viia poliitikat, mis on suunatud järgmistele eesmärkidele:', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 687, '3. Kodanikeühenduste laiem ja lihtsam kaasamine:', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (687, 688, 'teeme kodanike ja vabaühenduste paremaks kaasamiseks ühe portaali kaudu kättesaadavaks informatsiooni kõigi riigi tasandil toimuvate strateegiate ja arengukavade ning õigusaktide eelnõude kohta, näidates ära protsessi etapid, ajakava ning avalikkuse võimalused otsuse kujundamises osaleda;', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (687, 689, 'rakendame Eesti Kodanikuühiskonna arengukava (EKAK) ja „Kaasamise hea tava" põhimõtteid. Anname äriregistris vabaühenduste kohta olevad andmed vabalt kasutusse, et oleks lihtne leida organisatsioone, kes vajavad vabatahtlikke, kellele annetada ja keda kaasata.', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 690, '4. Kodanikühendustele kindel rahastamine:', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (690, 691, 'muudame riigi kogutavad andmed vabaühenduste kohta - aastaaruanded ja põhikirjad kättesaadavaks ning eristame registris vabaühendused ja avaliku võimu asutatud ja kontrollitavad mittetulundusühendused;', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (690, 692, 'analüüsime valitsuse kinnitatava tulumaksusoodustusega mittetulundusühenduste nimekirja pääsemise tingimusi;', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (690, 693, 'toetame jätkuvalt kodanikühiskonna organisatsioone ja algatusi Kodanikuühiskonna Sihtkapitali (KÜSK), kohaliku omaalgatuse programmi, hasartmängumaksu nõukogu ja muude rahastamiskanalite kaudu. Jätkame KÜSKi riigieelarvelist toetamist.', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 694, '5. Noortele rohkem õigusi: algatame ühiskonnas debati selle üle, kas ja kuidas oleks ühiskonnas stabiilse tasakaalu hoidmiseks mõistlik arvestada valimistel laste ja noortega. Algatame arutelu valimisea 16. aastani viimise üle.', 30);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 695, '6. Jätkuv koostöö kirikute ja kogudustega: teeme igakülgset koostööd kirikute ja kogudustega, eeskätt traditsiooniliste konfessioonidega Eestis. Näeme kirikut ja kogudusi aktiivsete partneritena teenuste osutamises Eesti elanikele.', 30);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (696, 'Riigihalduse tänapäevastamisel peab Valitsusliit oluliseks järgmiste struktuursete reformide ja poliitikate elluviimist:', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 697, '7. Riigiga suhtlemine kiiremaks ja mugavamaks: muudame lihtsamaks inimeste ja ühingute suhtlemist riigiga, seda eelkõige e-riiki ja e-teenuseid arendades. Sõltumata elukohast peab riik ja omavalitsus olema igaühele käeulatuses.', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 698, '8. Eriseisundi ja -õigustega ametnike vähendamine, nende hüvede ühtlustamine võrreldes erasektoriga ning avaliku teenistuse muutmine nüüdisaegselt dünaamiliseks:', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (698, 699, 'võtame vastu uue Avaliku teenistuse seaduse, mis korrastab avaliku teenistuse, muutes läbipaistvaks ja võrreldavaks avaliku sektori palgasüsteemi ja ametihüvede kasutamise;', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (698, 700, 'täidame ametikohad avalikus teenistuses ja riigile kuuluvate ettevõtete juhatustes konkursi korras ning soodustame kvalifitseeritud tööjõu liikumist avaliku ja erasektori vahel;', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (698, 701, 'vähendame avalikus sektoris hõivatud inimeste hulka uute tehnoloogiate ja tõhusama töökorralduse kasutuselevõtu teel, mitte teenuste kvaliteedi arvel;', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (698, 702, 'koondame avaliku teenistuse seaduse rakendamise, samuti riigi kui tööandja tegevuse koordineerimise eest vastutuse ja kompetentsi Rahandusministeeriumisse.', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 703, '9. Riigihalduse muutmine lihtsamaks ja tõhusamaks:', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (703, 704, 'parandame riigi poliitikate strateegilist planeerimist ja ministeeriumide vahelist koostööd. 2014. aastal algava Euroopa Liidu eelarveperioodi vahendite tulemuslikumaks ja tõhusamaks kasutamiseks tugevdame keskset koordineerimist, koondame funktsioone ja vähendame juhtimistasandeid. Võimalusel koostame kõikide fondide kohta ühe arengukava, mis muuhulgas tagab rakendamise käigus suurema paindlikkuse vahendite ümbertõstmiseks:', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (704, 705, 'tagame avalik-õiguslike isikute ja riigi poolt asutatud sihtasutuste tegevuse läbipaistvuse, aruandluse ja juhtorganite vastutuse;', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (704, 706, 'tõhusama juhtimise ja haldamise saavutamiseks koondame 2011. aasta lõpuks kogu riigiasutustele mittevajaliku kinnisvara ning 2013. aastaks riigiasutuste poolt kasutatava kinnisvara (välja arvatud sümbolkinnisvara) ja selle haldamise ning arendamisega seotud tegevused Riigi Kinnisvara Aktsiaseltsi;', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (704, 707, 'koostöös erasektoriga jätkame ministeeriumide ja nende valitsemisealade tugiteenuste (raamatupidamine, personalitöö, hanked jmt) koondamist ühtsetesse keskustesse, pakkudes sama teenust ka kohalikele omavalitsustele ning sulgeme mittevajalikud asutused. Võtame riigi tugitegevuses kasutusele ühtsed standardid ja infosüsteemid. Toetame omavalitsuste tugifunktsioonide (õigusteenistus, raamatupidamine) konsolideerimist;', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (704, 708, 'määrame riigihangete läbiviimiseks kompetentsikeskuste näol kesksed hankijad ja läheme üle e-hangete korraldamisele hiljemalt aastaks 2014.', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 709, '10. Omavalitsuste, kodanikeühenduste ja erasektori suurem kaasamine riigivalitsemisse:', 31);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (709, 710, 'anname kodanikeühendustele ja erasektorile võimaluse täita senisest suuremat rolli sotsiaalhoolekandes, kultuuri-, spordi-, keskkonnakaitse, tervishoiu- ja turvalisuse vallas, hariduses ning teistes valdkondades. Seal kus võimalik, anname riiklikke ülesandeid omavalitsustele, kodanikeühendustele ja eraettevõtetele üle anda halduslepingute alusel.', 31);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (711, 'Kohalik omavalitsus ei ole riigi käepikendus, vaid vabade kodanike iseseisev kohalik valitsus. Eesti Vabariigi Põhiseadusega, Kohalike omavalitsuste korralduse seadusega, Euroopa kohaliku omavalitsuse harta ratifitseerimisega on Eesti valinud ja kinnistanud autonoomse, kogukonnapõhise kohaliku omavalitsuse. Tugev kohalik identiteet, laiapõhjaline demokraatlik riigikorraldus, keskvõimu tasakaalus hoidmine on väga suured väärtused, millel põhineb Eesti iseseisvus. ', 32);
INSERT INTO lubadus(lubadusid,mustpealkiri,valdkondid) VALUES (712, 'Kohaliku halduse tänapäevastamisel peab Valitsusliit oluliseks reformide elluviimist järgmistest põhimõtetest lähtuvalt:', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 713, '11. Omavalitsuste koostöö ja omavalitsuste ühisasutuste moodustamise soodustamine seaduse ja lepingute alusel riiklike ning mitut omavalitsust hõlmavate omavalitsuslike ülesannete täitmiseks;', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 714, '12. Omavalitsustele iseseisva tulubaasi, regionaalse tasakaalustatuse ja kohaliku demokraatia suurendamine:', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (714, 715, 'viime omavalitsuste tulubaasi pikaajalise planeerimise Riigieelarve strateegiasse;', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (714, 716, 'eelarvevõimaluste tekkel vaatame üle omavalitsuste tulubaasi kujunemise alused võttes arvesse omavalitsuste koonddefitsiiti. Üldise suundumusena peab tänaste väikeste kohalike maksutulude, suure osatähtsusega riigitoetuste ja niinimetatud kohaliku iseloomuga riiklike maksude asemel oluliselt suurenema omavalitsuste roll oma maksutulu kujundamisel;', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (714, 717, 'Omavalitsustele riigieelarvest investeeringutoetuste eraldamisel võtame arvesse omavalitsuste finantssuutlikkust (sh võlakoormus piirmäärast kinnipidamine);', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (714, 718, 'võimaldame igal Eesti elanikul omandi, töökoha või perekondlike sidemete alusel määrata kuni kaks ametlikku elukohta ja sellest tulenevalt jagada nende vahel tulumaksu;', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (714, 719, 'seame eesmärgiks suurendada kohalike teede hoiu rahastamise osakaalu üldises teehoiu rahastamises;', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (714, 720, 'tihendame koostööd kohalike omavalitsustega kvaliteetsete e-teenuste arendamisel.', 32);
INSERT INTO lubadus(ylemlubadusid,lubadusid,mustpealkiri,valdkondid) VALUES (683, 721, '13. Aitame arendada ja finantseerida omavalitsuste arengu mõttekoda, kus kohtuvad teadlased, ametnikud, poliitikud omavalitsejad ja vabasektor.', 32);
